INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Angoulême 2013 - Aurélie Filippetti (Ministre de la Culture) : "L’essentiel est que la diversité du marché éditorial de la BD soit conservée." ', 'Profitant de la visite du Ministre de la Culture Aurélie Filippetti au Festival International de la Bande Dessinée d’Angoulême, nous avons eu l’occasion de la rencontrer. Elle nous raconte son rapport à la BD et nous annonce des mesures concrètes pour la soutenir.', 'http://www.actuabd.com/Angouleme-2013-Aurelie-Filippetti', 1, 'Didier Pasamonik', '2013-2-6', 'Aurélie Filippetti; Festival BD d\'Angoulême; BD; bande dessinée; polémique', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti : la BD, c’est pour les enfants, et c’est pas du tout en crise', 'Aurélie Filippetti a déclenché une polémique à retardement au festival de BD d’Angoulême, dans une interview publiée mercredi par un site spécialisé. Interrogée sur la valeur culturelle de la bande dessinée, la ministre de la Culture a choqué auteurs et bédéphiles en déclarant que le neuvième art était « un art populaire et une manière de faire lire les enfants »', 'http://www.rue89.com/rue89-culture/2013/02/07/la-bd-pour-les-enfants-et-pas-du-tout-en-crise-selon-aurelie-filipetti', 3, 'François Oulac', '2013-2-7', 'Aurélie Filippetti; Festival BD d\'Angoulême; BD; bande dessinée; polémique', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti, la BD et les médias : retour sur un clash en quatre actes', 'Aurélie Filippetti sait manier le "mot-dièse" : #çasuffit, #gardezvosleçons, #malhonneteteintellectuelle, #marredelintox... La ministre de la Culture a violemment pris à partie plusieurs médias sur le réseau social Twitter vendredi 8 février. En cause, la reprise par Télérama, Rue89 ou encore ActuaLitté de certaines de ses déclarations sur la BD, prononcées en marge du festival d\'Angoulême. Francetv info déroule un clash en quatre actes.', 'http://www.francetvinfo.fr/aurelie-filippetti-la-bd-et-les-medias-retour-sur-un-clash-en-quatre-actes_247083.html', 4, 'NULL', '2013-2-8', 'Aurélie Filippetti; Festival BD d\'Angoulême; BD; bande dessinée; polémique', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Après Angoulême, Aurélie Filippetti répond aux critiques', 'Ce matin, la ministre de la Culture, Aurélie Filippetti a interpellé directement Télérama et d\'autres médias comme Rue89, via son compte Twitter.', 'http://www.telerama.fr/livre/apr-s-angoul-me-aur-lie-filippetti-r-pond-aux-critiques,93298.php#xtor=RSS-22', 5, 'David Carzon', '2013-2-8', 'Aurélie Filippetti; Festival BD d\'Angoulême; BD; bande dessinée; polémique', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti et la bande dessinée, une histoire compliquée', 'La Ministre de la Culture Aurélie Filippetti crée la polémique sur Twitter pour des propos tenus lors d\'une interview réalisée au Festival de la bande dessinée d\'Angoulême. ', 'http://www.lexpress.fr/culture/livre/aurelie-filippetti-et-la-bande-dessinee-une-histoire-compliquee_1218556.html', 6, 'Sophiane Meddour', '2013-2-8', 'Aurélie Filippetti; Festival BD d\'Angoulême; BD; bande dessinée; polémique', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti dévoile ses orientations pour le livre', 'Le magazine Livres Hebdo publie une interview du ministère de la Culture et de la Communication dans son édition du vendredi 18 janvier.', 'http://www.livreshebdo.fr/politique-du-livre/actualites/aurelie-filippetti-devoile-ses-orientations-pour-le-livre/9933.aspx', 7, 'fp', '2013-1-17', 'Aurélie Filippetti; livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"En dépit des étoiles", de Michel Quint ', 'A Lille, l\'affaire des noyés de la Deûle demeure mystérieuse. A partir de ces faits divers tragiques, l\'écrivain Michel Quint, l\'auteur du best-seller "Effroyables jardins", publie un roman-enquête. Il entraîne le lecteur dans les nuits arrosées de Lille où se côtoient footballeurs, mafieux et prostituées. Une réussite.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-10-02-888119-2013-02-10', 8, 'Philippe Vallet', '2013-2-10', 'Policier; polar; Michel Quint; En dépit des étoiles', '22', '0', '0');
