INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Johnny Hallyday balance dans son livre : est-il lui aussi "un vieux con réac" ?', 'LA QUESTION DU JOUR. Johnny Hallyday a écrit un livre ! Dans "Dans mes yeux", rédigé avec Amanda Sthers, Johnny règle ses comptes. Tout le showbiz y passe, de Claude François à Michel Sardou ("vieux con réac") en passant par son fils David Hallyday et son ex-femme Adeline. Mais pourquoi Johnny est-il aussi méchant ? On fait le tour de la question.', 'http://leplus.nouvelobs.com/contribution/776716-johnny-hallyday-balance-dans-son-livre-est-il-lui-aussi-un-vieux-con-reac.html', 10, 'Mathieu Sicard', '2013-2-6', 'Johnny Hallyday; biographie; Dans mes yeux', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Je vous ai compris", la nouvelle BD interactive ', 'Lors de notre reportage sur le Festival International de la Bande Dessinée d\'Angoulême (que vous avez peut être vu en vidéo), nous avions croisé Franck Chiche et Laurent Thiry, respectivement réalisateur et producteur du film "Je vous ai compris" diffusé sur Arte.', 'http://www.7bd.fr/2013/02/je-vous-ai-compris-la-nouvelle-bd.html?spref=fb', 12, 'NULL', '2013-2-8', 'BD; film d\'animation; innovation; Je vous ai compris', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Je vous ai compris"', 'Film "graphique", selon l\'adjectif employé par ses concepteurs, Je vous ai compris est un objet entièrement composite. Il est d\'autant plus difficile à saisir qu\'il a pour toile de fond une période, celle de la guerre d\'Algérie, qui reste éminemment complexe et sensible.', 'http://www.lemonde.fr/culture/article/2013/02/01/je-vous-ai-compris_1822650_3246.html', 13, 'Jean-Baptiste de Montvalon', '2013-2-1', 'BD; film d\'animation; innovation; Je vous ai compris', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le roman de Stephen King sorti en mars 2011 en France, est l\'objet d\'une adaptation télévisée. Premières images. ', 'Le roman Under the dome de Stephen King prend vie à la télévision, dans une mini-série diffusée par CBS à l\'été 2013, réalisée par Brian K. Vaughan, scénariste de Lost. ', 'http://www.lexpress.fr/culture/tele/le-dome-de-stephen-king-teaser-de-la-serie-tele_1216769.html', 6, 'NULL', '2013-2-4', 'Dôme; Stephen King; SF; Science-Fiction', '25', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le scénariste de "Quai d\'Orsay" tombe le masque ', 'Le mystérieux Abel Lanzac, scénariste à l\'identité cachée de la BD "Quai d\'Orsay", a tombé le masque dimanche au Festival d\'Angoulême: il s\'appelle Antonin Baudry et fut l\'une des plumes de Dominique de Villepin au ministère des Affaires étrangères.', 'http://www.lepoint.fr/ces-gens-la/le-scenariste-de-quai-d-orsay-tombe-le-masque-03-02-2013-1623384_264.php', 15, 'AFP', '2013-2-3', 'Festival BD d\'Angoulême;  bande dessinée; Quai d\'Orsay; Abel Lanzac; Antonin Baudry', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Dans les coulisses de « Quai d’Orsay » : de Blain à Tavernier, Villepin change de main ', 'Voir le tome 2 de la géniale chronique du Quai d’Orsay sous Dominique de Villepin récompensé du Prix du meilleur album au festival d\'Angoulême n\'a rien d\'étonnant. Retrouver Thierry Lhermitte devant la caméra et Bertrand Tavernier derrière pour la version cinéma est plus surprenant. Du coup, on a eu envie d’aller y voir de plus près. Récit d’une journée de tournage sous les ors de la République, avec, en prime, le réalisateur croqué par le dessinateur Christophe Blain.', 'http://www.evene.fr/cinema/actualite/dans-les-coulisses-de-quai-d-orsay-de-blain-a-tavernier-v-1844683.php', 16, 'Etienne Sorin', '2013-1-30', 'bande dessinée; Quai d\'Orsay; film; Christophe Blain', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Rencontre avec l\'auteur de BD Marion Montaigne', '"Tu mourras moins bête, mais tu mourras quand même!", c\'est le slogan du blog de Marion Montaigne, adapté sur papier par les éditions Ankama.', 'http://www.telerama.fr/livre/rencontre-avec-l-auteur-de-bd-marion-montaigne,92989.php', 5, 'NULL', '2013-2-3', 'bande dessinée; Marion Montaigne; Tu mourras moins bête', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« À boire et à manger » et « Tu mourras moins bête » s’épanouissent sur papier', 'Avec humour, l’un traite de cuisine, l’autre de science. Tous deux l’ont d’abord fait sur leurs blogs, avant de se voir publier. Guillaume Long sort le second tome de A boire et à manger, tandis que Marion Montaigne donne une suite à Tu mourras moins bête.', 'http://www.bodoi.info/news/2013-01-10/a-boire-et-a-manger-et-tu-mourras-moins-bete-reviennent-sur-papier/64410', 17, 'Laurence Le Saux', '2013-1-10', 'bande dessinée; Marion Montaigne; Tu mourras moins bête; A boire et à manger; Guillaume Long; recettes de cuisine', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Cercle Polar #113 : “Etranges Rivages” d’Arnaldur Indridason & “Froid mortel” de Johan Theorin', 'Vous perdrez-vous dans les “Etranges Rivages” du dernier Indridason ou préférerez-vous frissonner dans le “Froid mortel” de l\'étrange hôpital mis en scène par le Suédois Johan Theorin ? Cercle polar vous aide à choisir.', 'http://www.telerama.fr/livre/cercle-polar-113-etranges-rivages-d-arnaldur-indridason-froid-mortel-de-johan-theorin,92550.php', 5, 'NULL', '2013-2-1', 'polar; policier; Etranges rivages; Arnaldur Indridason; Froid mortel; Johan Theorin', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '«Le Grand Complot» de Laurent Joffrin', 'En fin connaisseur de l\'épopée napoléonienne, Joffrin s\'amuse et nous amuse, en réconciliant l\'histoire avec le polar. ', 'http://bibliobs.nouvelobs.com/les-ecrivains-de-l-obs/20130125.OBS6651/le-grand-complot-de-laurent-joffrin.html?xtor=RSS-15', 18, 'NULL', '2013-1-25', 'Laurent Joffrin; Napoléon; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Assassinés" de Jean-Christophe Buisson', 'Beaucoup d\'hommes et de femmes politiques ont trouvé la mort dans des circonstances tragiques. Dans son nouveau livre, Jean-Christophe Buisson, chef des pages culture du "Figaro Magazine", raconte avec talent quinze de ces assassinats qui ont bouleversé le cours de l\'histoire. ', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-04-02-jean-christophe-buisson-%C2%96-assassines-882083-2013-02-04', 8, 'Philippe Vallet', '2013-2-4', 'Jean-Christophe Buisson; histoire; crime', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aux frontières de la soif, de Kettly Mars', 'Au début, on se demande si c’est un sale type. Un architecte friqué, qui a écrit un livre à succès (un seul) avant de perdre l’inspiration, puis de se perdre, lui, dans le campement de fortune de Canaan, véritable ville dans la ville, immense bidonville abritant toute la misère du monde de Haïti après le tremblement de terre de 2010. Oui, Fito Belmar se perd, et on n’a aucune envie de le suivre, dans des amours tarifées avec de très jeunes filles. Des enfants, disons-le.', 'http://www.tsfjazz.com/albernhe-tsfblog/?p=605', 19, 'Laure Albernhe', '2013-1-31', 'Kettly Mars; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La glorieuse incertitude des chiffres', 'Chaque année, c’est la foire d’empoigne : le premier à dégainer, c’est Gilles Ratier, avec son désormais célèbre rapport pour l’Association des Journalistes et des Critiques de BD (ACBD), ensuite Livres-Hebdo qui avance son interprétation des chiffres Ipsos, en même temps que le communiqué de GfK sur le marché de la BD. Aucune de ces études ne permet de dire que la BD va mal ou bien. Enquête.', 'http://www.actuabd.com/Marche-de-la-BD-2012-La-glorieuse', 2, 'Didier Pasamonik', '2013-2-15', 'chiffres; marché de la BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '“Sur la tombe de ma mère”, livre étonnant de Jean Gab’1', 'En 2003, un rappeur au blase de tonton flingueur sortait un des disques les plus touchants de la décennie. Un album de titi à l’ancienne qui déroulait une vie pas drôle en un long poème bétonné, fustigeant au passage la scène française dans J’t’emmerde, resté comme une plaie ouverte. Dix ans après le son, voici le texte : papa qui bute maman dans un parking, la DDASS, la fratrie dispersée, la haine du père et la rage au ventre.', 'http://www.lesinrocks.com/2013/02/14/musique/sur-la-tombe-de-ma-mere-livre-etonnant-de-jean-gab1-11360791/', 20, 'Thomas Blondeau', '2013-2-14', 'Jean Gab’1; roman; rappeur', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Profanes, ou le livre des morts de Jeanne Benameur', 'Dans Profanes, la romancière Jeanne Benameur met en scène un nonagénaire qui organise ses jours comme au temps où il était chirurgien. ', 'http://www.lexpress.fr/culture/livre/profanes-ou-le-livre-des-morts-de-jeanne-benameur_1219791.html', 6, 'François Busnel', '2013-2-13', 'Jeanne Benameur; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livres : les ravages des critiques sur Amazon', 'DÉCRYPTAGE - Un Américain accuse les mauvaises critiques sur le site marchand d\'avoir tué les ventes de son livre. Les commentaires, bons ou mauvais, de ce site influencent de plus en plus le choix de lecture sans pour autant être fiables.', 'http://www.lefigaro.fr/le-live/2013/02/12/03018-20130212ARTFIG00428-livres-les-ravages-des-critiques-sur-amazon.php', 21, 'Laurène Daycard', '2013-2-12', 'Amazon; critiques de livres', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Où en est le livre numérique en France ?', 'L\'Université Paris-Dauphine organise ce mercredi deux tables rondes autour de la question "Livre numérique, chapitre 2 : une page qui se tourne pour le papier ?"', 'http://www.atlantico.fr/decryptage/ou-en-est-livre-numerique-en-france-helene-vedrine-636910.html#lgTHY3QMA2RWapGi.30', 22, 'Hélène Védrine', '2013-2-13', 'livre numérique; table ronde', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Marché de la BD 2012 : De quels chiffres parle-t-on ?', 'La semaine dernière a été marquée par la bronca d’une certaine catégorie d’auteurs contre les propos de la ministre de la culture Aurélie Filippetti au sujet du marché de la BD. Elle constatait que le 9e art allait plutôt bien dans un marché du livre en net recul. En face, certains auteurs constataient en revanche une situation de misère qui contrastait avec un optimisme impulsé par l’ambiance du festival angoumoisin.', 'http://www.actuabd.com/Marche-de-la-BD-2012-De-quels', 2, 'Didier Pasamonik', '2013-2-13', 'chiffres; marché de la BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La chronique de Gérard Collard – 22 Britannia Road', 'Avant de partir en vacances, notre libraire Gérard Collard ne vous a pas oublié. Il vous présente aujourd’hui le dernier livre d’Amanda Hodgkinson « 22 Britannia Road«   aux éditions Belfond', 'http://www.lesdeblogueurs.tv/culture/livre-la-chronique-de-gerard-collard-22-britannia-road/', 23, 'Gérard Collard', '2013-2-11', 'Gérard Collard; La Griffe Noire; Amanda Hodgkinson; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La chronique de Jean-Edgar Casel – Bluberry & Phil Perfect', 'Une des passions de notre libraire-chroniqueur Jean-Edgar Casel est la bande-dessinée… Et s’il lui fallait ne garder que deux bandes-dessinées, « Bluberry : L’intégrale Tome 1 » de Jean Giraud et Jean-Michel Charlier (éditions Dargaud) et « Phil Perfect : L’intégrale Tome 1 » de Serge Clerc (éditions Dupuis) en ferait partis…', 'http://www.lesdeblogueurs.tv/culture/livre-la-chronique-de-jean-edgar-casel-bluberry-phil-perfect/', 23, 'NULL', '2013-2-10', 'Jean-Edgar Casel; La Griffe Noire; Bluberry; Phil Perfect; bande dessinée; BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Un gros livre d\'enfer" de Matt Groening chez Fetjaine', 'Génie du dessin animé d\'humour, cet album permet de découvrir le Matt Groening dessinateur de comic strips, dans la lignée des Spiegleman et autres Crumb. On y retrouve son humour un peu dingue, grinçant, parfois noir et sa critique imparable de l\'absurdité de nos sociétés occidentales. ', 'http://www.20minutes.fr/article/1101847/un-gros-livre-enfer-matt-groening-chez-fetjaine-paris-France', 24, 'NULL', '2013-2-16', 'Matt Groening; bande dessinée; BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Alessandra Sublet tacle le livre de Johnny Hallyday', 'L\'animatrice Alessandra Sublet n\'a pas du tout aimé le livre de Johnny Hallyday et l\'a fait savoir ouvertement dans son émission ce mercredi 13 février.', 'http://www.staragora.com/news/alessandra-sublet-tacle-le-livre-de-johnny-hallyday/458776', 25, 'NULL', '2013-2-15', 'Alessandra Sublet; biographie; Johnny Hallyday; Dans mes yeux', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Suite française » d’Irène Némirovsky : un livre fascinant !', 'Ce livre, prix Renaudot 2004, est un petit miracle en soi. Son auteur, Irène Némirovsky, est morte à Auschwitz en août 1942, suivie quelques mois plus tard par son mari, Michel Epstein. Irène Némirovsky, brillant écrivain de l’entre – deux – guerres, dont les parents avaient fui la Russie après la révolution d’octobre, avait trouvé dans notre pays une terre d’accueil en même temps qu’une terre d’élection. ', 'http://blogs.lexpress.fr/les-8-plumes/2013/02/15/suite-francaise-direne-nemirovsky-un-livre-fascinant/', 6, 'les-8-plumes', '2013-2-15', 'Irène Némirovsky; prix Renaudot; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L\'histoire d\'un livre: «Gangs Story»', '« Gangs Story » c’est un livre dont les textes sont du dénommé Kizo et les photographies de Yan Morvan. Kizo est né, a grandi et vit au milieu des bandes de la banlieue parisienne. Morvan, lui a suivi pendant 40 ans, l’évolution d’une jeunesse : des blousons noirs aux rockers, aux bikers, aux émeutes… De leur rencontre nait une histoire vraie. Un livre rare, déjà culte.', 'http://blogs.mediapart.fr/blog/michel-puech/150213/lhistoire-dun-livre-gangs-story', 26, 'Michel Puech', '2013-2-15', 'Yan Morvan; Kizo; sociologie; gang', '1', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'France: un livre sur le cholestérol fait polémique', 'Si l’excès de cholestérol n’était finalement pas dangereux pour la santé ? C’est la thèse défendue par le professeur de médecine Philippe Even dans un livre intitulé La vérité sur le cholestérol qui doit paraître le 21 février 2013. Un ouvrage qui fait déjà polémique, comme le précédent où il affirmait que la moitié des médicaments existants sont inutiles.', 'http://www.rfi.fr/france/20130214-france-livre-le-cholesterol-fait-polemique', 27, 'NULL', '2013-2-14', 'Philippe Even; santé; cholestérol', '23', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Cercle Polar #114 : “La Place des morts” de Sylvie Granotier et “L\'Expatriée” d\'Elsa Marpeau', 'Trois univers totalement différents, entre la vie faussement tranquille dans les condos chics de Singapour, les inquiétudes d\'une avocate parisienne et le destin brutal d\'un jeune boxeur en banlieue. Bleu, blanc, rouge et surtout noir. ', 'http://www.telerama.fr/livre/cercle-polar-114-la-place-des-morts-de-sylvie-granotier-et-l-expatriee-d-elsa-marpeau,93543.php', 5, 'NULL', '2013-2-16', 'Sylvie Granotier; Elsa Marpeau; polar; thriller', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Bande-dessinée : le « doc » de Maiana Bidegain fait des bulles au ministère ', 'Hier soir, la réalisatrice bayonnaise Maiana Bidegain présentait son documentaire sur l’envers du monde de la bande dessinée : « Sous les bulles ». Cela après son avant-première lors du Festival de la BD d’Angoulême. ', 'http://www.sudouest.fr/2013/02/16/le-doc-fait-des-bulles-au-ministere-968297-790.php', 28, 'Pierre Penin', '2013-2-16', 'Maiana Bidegain; reportage; BD; bande dessinée; Festival BD d\'Angoulême', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Quand la BD vient en aide aux enfants malades de Lyon', 'Un illustrateur de bande dessinée est allé rendre visite aux enfants cancéreux du centre Léon-Bérard. L\'occasion de partager avec eux "Boule à Zéro", sa BD qui traite de leur quotidien.', 'http://www.metrofrance.com/lyon/quand-la-bd-vient-en-aide-aux-enfants-cancereux-de-lyon/mmbn!EPEy4vInFTCA/', 29, 'Pierre-Alexandre Bevand', '2013-2-14', 'Boule à Zéro; Serge Ernst; journée mondiale de lutte contre les cancers de l’enfant; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Interview : Marc Levy son nouveau roman et sa vision du livre numérique', 'Le dernier roman de Marc Levy : «Un sentiment plus fort que la peur» (lire notre chronique) vient de paraitre simultanément en version ebook chez Versilio et en version papier chez Robert Laffont. A l’occasion de la sortie du livre, Marc Levy s’est prêté au jeu des questions réponses, son approche sur le numérique est intéressante et il va même jusqu’à proposer un modèle économique aux éditeurs.', 'http://www.idboox.com/interviews/interview-marc-levy-son-nouveau-roman-et-sa-vision-du-livre-numerique/', 30, 'IDBOOX', '2013-2-16', 'Marc Levy; interview; roman; livre numérique', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Lecture : “Underground” de Haruki Murakami', 'C\'est un livre plein de larmes, un livre de parole vive, puisée au plus près de sa source : les victimes et les auteurs de l\'attentat au gaz sarin dans le métro de Tokyo, le 20 mars 1995. Fabienne Bussaglia lit un extrait d\'“Underground”, de Haruki Murakami.', 'http://www.telerama.fr/livre/lecture-underground-de-haruki-murakami,93595.php', 5, 'NULL', '2013-2-17', 'Haruki Murakami; roman; secte; attentat', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Misma, la relève de la BD alternative', 'Coup de projecteur sur Misma! Installée en Midi-Pyrénées, la petite structure éditoriale a reçu le Prix de la BD alternative au dernier festival d\'Angoulême.', 'http://www.franceinfo.fr/livre/bd-bande-dessinee/misma-la-releve-de-la-bd-alternative-892275-2013-02-17', 8, 'Jean Christophe Ogier', '2013-2-17', 'Misma; bande dessinée; BD alternative', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Bon-Encontre : Ingrid Astier prix 2013 Calibre 47 - Nicoby prix Polar-Encontre et Eloïse Varin prix "Plumes Noires" " Sud Ouest"', 'Les trois lauréats ont été récompensés samedi matin lors de \'inauguration du salon du polar qui se tient tout ce week-end au centre culturel Jacques Prévert.', 'http://www.sudouest.fr/2013/02/16/bon-encontre-ingrid-astier-prix-2013-calibre-47-nicoby-prix-polar-encontre-et-eloise-varin-prix-plumes-noires-sud-ouest-968818-3603.php', 28, 'Maryan Charruau', '2013-2-16', 'salon du polar d\'Agen; Eloise Varin; Ingrid Astier; Nicoby', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Un exilé heureux', 'Jake Lamar est resté fidèle au football américain et au basket de la NBA. « Mais , dit-il, pour le journal Libération , j’ai fait un article sur Mourad Boudjellal, le président du Rugby club toulonnais, que je suis allé rencontrer à Toulon. Ç’a été ma première initiation au rugby… » Et une belle preuve d’ouverture d’esprit, pour un Afro-Américain de Brooklyn !', 'http://www.republicain-lorrain.fr/actualite/2013/02/17/un-exile-heureux', 31, 'Richard Sourgnes', '2013-2-17', 'Jake Lamar; thriller', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La "surproduction" est-elle responsable de la paupérisation des auteurs ?', 'Nous achevons notre réflexion sur le marché de la BD 2012 sur cette question : la "surproduction" est-elle responsable d’une paupérisation des auteurs ? Car pour fournir 5560 albums dans l’année, il a bien fallu les créer, cela s’est-il fait dans des conditions défavorables à la profession ? Petit tour de la question.', 'http://www.actuabd.com/Marche-de-la-BD-2012-La', 2, 'Didier Pasamonik', '2013-2-18', 'chiffres; marché de la BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Andreas, créateur d’univers rares', 'Artiste étonnant, inclassable, Andreas creuse depuis une soixantaine d’albums un sillon très particulier. Avec notamment Rork (dont il a récemment publié une aventure, Les Fantômes), un“voyageur” âgé de plusieurs siècles, qui semble trentenaire et comprend les rêves des autres. Ou Capricorne, un astrologue issu de la série Rork. Entretien avec un fin ciseleur de puzzles graphiques, féru d’architecture — auquel une exposition a été consacrée à Saint-Malo et Angoulême.', 'http://www.bodoi.info/a-la-une/2013-02-18/andreas-createur-dunivers-rares/65626', 17, 'Laurence Le Saux', '2013-2-18', 'Andreas; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Six romans en lice pour le prix du polar historique', 'Lancé en 2012 dans le cadre du salon du livre de Montmorillon, le prix du polar historique associe cette année quatre médiathèques. Leurs lecteurs sont invités à prendre part à cet événement culturel de premier plan.', 'http://www.lanouvellerepublique.fr/Toute-zone/Loisirs/Livres-cd-dvd/n/Contenus/Articles/2013/02/18/Six-romans-en-lice-pour-le-prix-du-polar-historique-1340260', 32, 'Vincent Ribault', '2013-2-18', 'prix du polar historique; thriller; Montmorillon', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Marc Levy, indésirable président du Salon du livre de Québec', 'La colère du milieu littéraire québécois contre la nomination de Marc Levy à la présidence d\'honneur du Salon international du livre de Québec (Silq, du 10 au 14 avril) ne retombe pas. ', 'http://www.lexpress.fr/culture/livre/marc-levy-indesirable-president-du-salon-du-livre-de-quebec_1222320.html', 6, 'NULL', '2013-2-19', 'Marc Levy; Salon international du livre de Québec; polémique', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Un nouveau James Bond... en livre', 'Le titre et le sujet du roman n\'ont pas été divulgués, mais les promoteurs du projet parlent d\'un "retour à l\'ère classique des James Bond" et précisent que William Boyd a inscrit son récit en 1969.', 'http://lci.tf1.fr/culture/livres/un-nouveau-james-bond-en-livre-7834977.html', 33, 'NULL', '2013-2-19', 'James Bond; Ian Fleming; William Boyd', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le centenaire d\'Albert Camus fêté en librairie', 'A défaut d\'une exposition (annulée sur fond de polémique à Marseille-Provence 2013), le centenaire de la naissance de l\'écrivain engagé est surtout fêté en librairie, avec de nombreuses publications prévues dans les mois qui viennent.', 'http://www.francetv.fr/culturebox/albert-camus-fete-son-centenaire-en-librairie-132671', 34, 'Laurence Houot', '2013-2-20', 'Albert Camus; hommage; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Profanes", de Jeanne Benameur', 'La crise donne une nouvelle coloration aux romans. Un nombre croissant de livres raconte comment on peut surmonter les difficultés du moment en se rassemblant. Témoin, le nouveau roman de Jeanne Benameur intitulé "Profanes".', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-20-02-897893-2013-02-20', 8, 'Philippe Vallet', '2013-2-20', 'Jeanne Benameur; roman; chronique; Profanes', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Les yeux plus grands que le ventre", de Jô Soares', 'Jô Soares est l\'un des auteurs les plus originaux du Brésil.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-17-02-895251-2013-02-17', 8, 'Philippe Vallet', '2013-2-17', 'Jô Soares; chronique; Les yeux plus grands que le ventre; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Uchronies, l\'Histoire avec des si…', 'L\'Histoire telle qu\'elle aurait pu se passer: l\'uchronie fait un tabac. De la mort de Lénine, en route pour la Russie, au sauvetage de JFK à Dallas, en passant par la fuite maquillée de Lady Di, les écrivains s\'en donnent à coeur joie. Stephen King en tête.', 'http://www.lexpress.fr/culture/livre/uchronies-l-histoire-avec-des-si_1222381.html', 6, 'Baptiste Liger', '2013-2-20', 'Uchronie; histoire', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Filippetti : pas un euro pour l\'expo Camus par Onfray', 'Depuis mai dernier, la polémique autour de l\'exposition Albert Camus à Marseille n\'en finit pas de rebondir. Tout d\'abord annulée par décision du directeur délégué de Marseille Provence 2013, Thierry Roche, finalement, elle aura bel et bien lieu. Benjamin Stora, l\'historien qui devait s\'en charger n\'a cependant pas eu le plaisir de cette responsabilité. ', 'http://www.actualitte.com/expositions/filippetti-pas-un-euro-pour-l-expo-camus-par-onfray-36027.htm', 35, 'Nicolas Gary', '2012-8-15', 'Albert Camus; hommage; Aurélie Filippetti', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Et si Lady Di échappait à la mort?', 'Dans La véritable histoire de Lady L., l\'auteure britannique Monica Ali imagine la vie de la Princesse Diana si elle n\'avait pas connu une fin tragique', 'http://www.lexpress.fr/culture/livre/et-si-lady-di-echappait-a-la-mort_1222417.html', 6, 'Emmanuel Hecht', '2013-2-20', 'Lady Di; uchronie; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'EXCLUSIF. DSK par Marcela Iacub : "Un être double, mi-homme mi-cochon"', 'La juriste a eu une liaison de 7 mois avec l’ex-directeur du FMI, dont elle tire un livre vertigineux. L’interview que "Le Nouvel Observateur" publie ce jeudi sera la seule qu’elle donnera sur cette affaire. Extraits.', 'http://tempsreel.nouvelobs.com/l-affaire-dsk/20130220.OBS9474/exclusif-dsk-par-marcela-iacub-un-etre-double-mi-homme-mi-cochon.html', 10, 'Eric Aeschimann', '2013-2-20', 'Marcela Iacub; DSK; roman; polémique', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le marché de la BD est-il saturé ?', 'Pour Georges Merel, libraire à Nantes, la réponse ne fait aucun doute : « Oui, en volume ! » Avec 5565 livres de BD publiés en 2012 – dont 4109 nouveautés –, le rapport annuel de l\'Association des critiques et journalistes de bande dessinée (ACBD) constate une augmentation de l\'offre de 4,28% par rapport à 2011. Un chiffre en hausse pour la dix-septième année consécutive, dans un secteur à l\'économie de plus en plus fragile.', 'http://romainbaro.blog.lemonde.fr/2013/02/21/le-marche-de-la-bd-est-il-sature/', 13, 'Romain Baro', '2013-2-21', 'marché BD; bande dessinée; ACBD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Ladivine", le dernier roman envoûtant de Marie Ndiaye', '"Ladivine", de Marie Ndiaye, prix Goncourt 2009 pour "Trois femmes puissantes" est un roman magnifique sur les origines et la filiation.', 'http://www.francetv.fr/culturebox/ladivine-le-dernier-roman-envoutant-de-marie-ndiaye-132779', 34, 'Laurence Houot ', '2013-2-23', 'Marie Ndiaye; Ladivine; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Ladivine', 'Une mère, une fille, une petite-fille, entravées par les liens du sang. Peut-on se défaire de ses origines ? Un roman à l\'écriture somptueuse, par une Marie NDiaye plus puissante que jamais.', 'http://www.telerama.fr/livres/ladivine,93314.php', 5, 'Nathalie Crom', '2013-2-16', 'Marie Ndiaye; Ladivine; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Les Teutons flingueurs', 'Après les polars américain, irlandais, suédois, les éditeurs parient sur le succès du polar allemand. Enquête, de notre envoyée spéciale à Berlin.', 'http://bibliobs.nouvelobs.com/polar/20130218.OBS9278/les-teutons-flingueurs.html?xtor=RSS-15', 18, 'Odile Benyahia-Kouider', '2013-2-18', 'Nele Neuhaus; polar; allemand', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '«Millénium», maintenant la BD', 'Avant sa publication, le 22 mars, nous avons lu le premier tome de la BD «Millénium», la trilogie de Stieg Larsson. En avant-première, une planche inédite de cette adaptation très réussie.', 'http://www.leparisien.fr/loisirs-et-spectacles/millenium-maintenant-la-bd-25-02-2013-2596925.php', 37, 'Christophe Levent', '2013-2-25', 'Millénium; Stieg Larsson; bande dessinée; BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Qui était Stéphane Hessel ?', 'Stéphane Hessel, ancien résistant et ancien ambassadeur de France, auteur d\'Indignez-vous, est mort ce mercredi à l\'âge de 95 ans. Retour sur une vie exceptionnelle.', 'http://www.lexpress.fr/culture/livre/qui-est-stephane-hessel_948889.html', 6, 'Laurent Martinet', '2013-2-27', 'Stéphane Hessel; hommage; Indignez-vous !', '8', '0', '0');
