INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Mort de Stéphane Hessel : "La France perd un immense patriote humaniste"', 'La rumeur qui bruissait dans la matinée sur les réseaux sociaux a été confirmée par son épouse : l\'ancien diplomate et résistant Stéphane Hessel est mort dans la nuit de mardi à mercredi à l\'âge de 95 ans. Les hommages n\'ont pas tardé à affluer, notamment sur Twitter.', 'http://www.lemonde.fr/disparitions/article/2013/02/27/mort-de-stephane-hessel-la-france-perd-un-immense-patriote-humaniste_1839544_3382.html', 14, 'NULL', '2013-2-27', 'Stéphane Hessel; hommage; Indignez-vous !', '8', '0', '0');
