INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Polars et romans noirs pour nuits blanches: "Il faut tuer Lewis Winter"', 'POLARS - Qui n\'a jamais, un jour, rêver de changer de métier?', 'http://www.huffingtonpost.fr/corine-pirozzi/polars-lewis-winter_b_2774429.html', 40, 'Corine Pirozzi', '2013-2-28', 'Malcolm R. Mackay; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Célimène », une BD girly signée Adeline Blondieau', 'Plus connue du grand public pour ses talents de comédienne, Adeline Blondieau nourrit une vraie passion pour l’écriture. Elle signe ainsi sa douzième bande dessinée en tant que scénariste. L’héroïne de la célèbre série télé tropézienne se consacre à la suite des aventures de « Célimène », une ado qui découvre à 16 ans qu’on lui a menti sur ses origines et qui se lance dans une longue quête identitaire semée d’embûches. ', 'http://blogs.rtl.be/frenchtouch/2013/02/28/celimene-une-bd-girly-signee-adeline-blondieau/', 41, 'aschildt', '2013-2-28', 'Adeline Blondieau; Johnny Hallyday; BD; bande dessinée; girly', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La tête à l\'envers avec "Ceux d\'en haut et ceux d\'en bas"', 'La terre est ronde et il y a ceux d\'en haut et ceux d\'en bas. Cette image intrigante et vieille comme le monde est joliment illustrée dans l\'album "Ceux d\'en haut et ceux d\'en bas", de Paloma Valdivia (La Joie de Lire).', 'http://www.francetv.fr/culturebox/livre-jeunesse-la-tete-a-lenvers-avec-ceux-den-haut-et-ceux-den-bas-132983', 34, 'Laurence Houot', '2013-2-28', 'Paloma Valdivia; jeunesse; enfants', '13', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Un roman comique allemand imagine le retour d\'Hitler', 'Le roman "Er ist wieder da" est un énorme succès en librairie..., mais est accusé par certains de banaliser le nazisme.', 'http://www.lepoint.fr/culture/un-roman-comique-allemand-imagine-le-retour-d-hitler-20-01-2013-1617747_3.php', 15, 'NULL', '2013-1-20', 'Timur Vermes; uchronie; Hitler; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Cercle Polar #115 : “La Vérité sur Frankie” de Tina Uebel et “Chiennes de vie” de Frank Bill', 'Un portrait au hachoir de l\'Amérique rurale ou l\'exploration au scalpel d\'une manipulation ? Deux textes plus noirs que policiers sont au menu de ce Cercle Polar.', 'http://www.telerama.fr/livre/cercle-polar-115-la-verite-sur-frankie-de-tina-uebel-et-chiennes-de-vie-de-frank-bill,94164.php', 5, 'NULL', '2013-3-1', 'Tina Uebel; Frank Bill; cercle polar; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Au cœur de la pègre écossaise', 'Glasgow est devenu une des valeurs sûres du polar britannique. Petit nouveau dans le roman noir, l\'Ecossais Malcolm Mackay y situe sa première intrigue et nous plonge dans l\'univers impitoyable de la mafia locale.', 'http://www.lemonde.fr/livres/article/2013/02/01/au-c-ur-de-la-pegre-ecossaise_1825371_3260.html', 13, 'Yann Plougastel', '2013-2-1', 'Malcolm R. Mackay; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Big K », c’est une tuerie !', 'Chaque mois, notre journal sélectionne un album, avant de délivrer, en décembre, son prix de la BD de l’année. En février, « Big K », de Nicolas Duchêne et Fabian Ptoma, a remporté nos suffrages.', 'http://www.leparisien.fr/espace-premium/culture-loisirs/big-k-c-est-une-tuerie-01-03-2013-2607605.php', 37, 'Pierre Vavasseur', '2013-3-1', 'Big K; Nicolas Duchêne; Fabian Ptoma; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La guerre après la guerre. "Yellow Birds", de Kevin Powers', 'Si la guerre d\'Irak est au coeur de Yellow Birds, on ne saura jamais ce qu\'en pense son narrateur. Ni les autres personnages de ce roman. Ni son auteur, l\'Américain Kevin Powers. En toile de fond, quelques peurs, quelques enthousiasmes, mais rien de plus. La guerre est ailleurs.', 'http://www.lemonde.fr/livres/article/2013/03/01/kevin-powers-la-guerre-apres-la-guerre_1840167_3260.html', 13, 'Nils C. Ahl', '2013-3-1', 'Kevin Powers; guerre; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Une sélection de polars', 'Aujourd\'hui dans "A livre ouvert", spéciale "polar" avec Jean-Paul Collet, libraire à Paris (La Boucherie, dans le 5e arrondissement) et Gérard Collard, libraire à Saint-Maur (La Griffe noire).', 'http://www.franceinfo.fr/livre/a-livre-ouvert/une-selection-de-polars-908255-2013-03-02', 8, 'Valérie Expert', '2013-3-2', 'Jean-Paul Collet; Gérard Collard; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La BD en mode rétro', 'Avec "Souvenirs de l\'empire de l\'ATOME", la série "Parker" ou encore "Histoire de ma fuite", la BD s\'habille à la mode rétro.', 'http://www.franceinfo.fr/livre/bd-bande-dessinee/bd-du-3-mars-avec-manga-906121-2013-03-03', 8, 'Jean Christophe Ogier', '2013-3-3', 'BD; bande dessinée; Souvenirs de l\'empire de l\'atome; Parker; Histoire de ma fuite', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Amazon absent du Salon du livre : un problème d\'image en Europe', 'C\'était l\'une des questions brûlantes de la conférence de presse qui présentait l\'édition 2013 du Salon du livre de Paris : où est donc passée la firme Amazon, qui avait tant fait parler d\'elle l\'an passé ? Pour la première fois dans la vie de la société américaine, le Salon du livre de Paris, en 2012, avait vu l\'apparition d\'un stand de 80 m2 : jamais sur un salon du livre dans le monde on n\'avait encore assisté à cela...', 'http://www.actualitte.com/societe/pas-de-salon-du-livre-pour-amazon-un-probleme-d-image-en-europe-40691.htm', 35, 'Nicolas Gary', '2013-3-3', 'Amazon; Salon du livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Alors, il est pour quand le nouveau Tintin ? ', 'Voici trente ans, le 3 mars 1983, Hergé posait son crayon pour l’éternité. Qu’en est-il aujourd’hui de son héritage ? Son œuvre représente-t-elle encore quelque chose pour notre génération ? Tintin bouge-t-il encore ? La réponse est oui, même si l’on se pose aujourd’hui les mêmes questions qu’il y a trente ans.', 'http://www.actuabd.com/Alors-il-est-pour-quand-le-nouveau', 2, 'Didier Pasamonik', '2013-3-3', 'Tintin; bande dessinée; Hergé', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livre : La chronique de Gérard Collard – Meurtre dans le boudoir', 'Gérard Collard vous avez déjà parlé il y a quelques temps de la sortie en format poche du livre de Frédéric Lenormand « La baronne meurt à cinq heures« . Aujourd’hui notre chroniqueur vous parle d’un autre livre de cette série du même auteur « Meurtre dans dans le boudoir » toujours aux éditions Le Masque', 'http://www.lesdeblogueurs.tv/culture/livre-la-chronique-de-gerard-collard-meurtre-dans-le-boudoir/', 23, 'Gérard Collard', '2013-3-1', 'Gérard Collard; chronique; Meurtre dans le boudoir; Frédéric Lenormand', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Voyage au centre de Paris", d\'Alexandre Lacroix', 'Chaque centimètre carré de Paris a une histoire. C\'est ce que rappelle Alexandre Lacroix, rédacteur en chef de Philosophie Magazine, dans son huitième roman. Une promenade dans la capitale qui mêle l\'histoire, la littérature, l\'amour d\'une femme et bien sûr de Paris.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-04-03-voyage-au-centre-de-paris-d%C2%92alexandre-lacroix-909685-2013-03-04', 8, 'Philippe Vallet', '2013-3-4', 'Alexandre Lacroix; Paris; visite guidée; balade', '29', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Mon Paris, ma mémoire", d\'Edgar Morin', 'Le très célèbre sociologue Edgar Morin aime Paris. Dans son nouveau livre, il raconte une partie de sa vie amoureuse, intellectuelle et politique à travers les quartiers de la capitale où il a vécu. Une ballade pleine d\'humour, d\'intelligence et de sensibilité.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-edgar-morin-904745-2013-02-27', 8, 'Philippe Vallet', '2013-2-27', 'Edgar Morin; Paris; balade', '29', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Heureux les heureux: "Une brillante galerie de portraits"', 'Virginie Bougant, jurée du Prix des lecteurs de L\'Express, a aimé le roman de Yasmina Reza, qu\'elle verrait bien adapté au cinéma. ', 'http://www.lexpress.fr/culture/livre/heureux-les-heureux-une-brillante-galerie-de-portraits_1222492.html', 6, 'Virginie33', '2013-3-1', 'Yasmina Reza; roman; Heureux les heureux', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Joël Dicker et Françoise Héritier, auteurs les plus lus en 2012', 'C\'est le moment du bilan pour le palmarès hebomadaire L\'Express/Tite-Live. Les grands vainqueurs de l\'année 2012 sont Joël Dicker, avec La Vérité sur l\'affaire Harry Quebert, et Françoise Héritier, avec Le Sel de la vie: lettre à un ami. ', 'http://www.lexpress.fr/culture/livre/joel-dicker-et-francoise-heritier-les-plus-lus-en-2012-pour-l-express-tite-live_1225986.html', 6, 'NULL', '2013-3-1', 'Joël Dicker; Françoise Héritier; palmarès; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Rayon Poche #173 : “Contrée indienne” de Dorothy M. Johnson', 'Loin du clinquant hollywoodien, Dorothy Johnson raconte une Amérique des pionniers, un quotidien où Blancs et Indiens tentent de trouver leur place. Un recueil de nouvelles mythiques et un cadeau bonus : deux inédits impeccables.', 'http://www.telerama.fr/livre/rayon-poche-173-contree-indienne-de-dorothy-m-johnson,94152.php#xtor=RSS-36', 5, 'Christine Ferniot', '2013-3-1', 'Dorothy M. Johnson; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Une bande dessinée sur le PSG version Qatar', 'Le PSG version QSI va avoir sa bande dessinée. Les éditions 12 bis publieront en effet le 28 Mars Paris vaut bien un Cheikh. ', 'http://brl-tv.blog.leparisien.fr/archive/2013/03/04/le-psg-version-qatar-a-sa-bande-dessinee.html', 37, 'NULL', '2013-3-5', 'PSG; Hatem Ben Arfa', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le Goncourt du premier roman à "Un homme effacé" d\'Alexandre Postel', 'Le Goncourt du premier roman 2013 a été décerné à Alexandre Postel pour son ouvrage "Un homme effacé" aux éditions Gallimard, a annoncé mardi l\'académie Goncourt à l\'AFP. Il a été élu avec sept voix sur dix.', 'http://www.francetv.fr/culturebox/le-goncourt-du-premier-roman-a-un-homme-efface-dalexandre-postel-133177', 34, 'NULL', '2013-3-5', 'Prix Goncourt; premier roman; Alexandre Postel', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'M-BD : un nouveau magazine pour les amateurs de bandes-dessinées', 'Le Magazine "M BD" est paru pour la première fois en kiosque le 28 février 2013. Ce nouveau venu au rayon bande-dessinée des libraires, n\'est pas totalement un inconnu des fans de bulles. Plusieurs numéros étaient déjà parus, il y a deux ans, au gré d\'une publication aléatoire distribuée gratuitement sur Lyon. Il trône désormais sur les présentoirs au côté du célèbre Fluide Glacial.', 'http://www.francetv.fr/culturebox/m-bd-des-nouveaux-venus-au-rayon-bd-133181', 34, 'Anthony Laurent', '2013-3-5', 'Magazine BD; nouveauté; lancement', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L\'armée de l\'ombre : "L\'hiver russe"', 'Contrairement à ce que pourrait laisser croire le titre, « <i>L’Armée de l’Ombre</i> » n’est pas consacrée à la Résistance, mais à l’armée allemande, et plus précisément à la Wehrmacht engagée sur le front russe durant l’hiver 1942. Cette bande dessinée, dont la sortie coïncide avec les 70 ans de la bataille de Stalingrad, se caractérise par un graphisme réaliste de grande qualité et un scénario des plus convaincants, loin de tout manichéisme. Suivez l’histoire d’un jeune allemand fraichement incorporé dans la 332e compagnie d’infanterie.', 'http://www.histoire-pour-tous.fr/bandes-dessinees/75-histoire/4484-larmee-de-lombre-l-lhiver-russe-r-bd.html', 42, 'Desaix', '2013-3-5', 'BD; 2ème guerre mondiale; Allemagne', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'A boire et à manger - Guillaume Long, l’interview', 'Certains aiment manger. Certains aiment dessiner. Guillaume Long, lui, peut faire les deux. Son blog, A boire et à manger, est une source d’inspiration pour ceux qui veulent préparer de bons petits plats tout en se payant une bonne tranche de rigolade.', 'http://bd.krinein.com/a-boire-manger-guillaume-long-linterview-20605.html', 43, 'Guillom', '2013-3-5', 'interview; cuisine; recettes', '6', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Rachid Santaki, le polar de l’autre côté du périf’', 'Depuis quelques semaines, ses affiches ont envahi presque tous les murs de banlieue. Le long des palissades, sur le périphérique, sur les rebords des routes, dans tous les départements de la banlieue parisienne, les murs proclament : « Le 93 a son roman le plus noir : flic ou caillera ». ', 'http://banlieue.blog.lemonde.fr/2013/03/06/rachid-santaki-le-polar-de-lautre-cote-du-perif/', 13, 'Sylvia Zappi', '2013-3-6', 'polar; banlieue; Rachid Santaki', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Des chiffres et des litres', 'Rachid Santaki est né en 1973 à Saint-Ouen, a grandi à Saint-Denis, c’est sans doute ce qui lui a donné l’énergie nécessaire pour être à la fois entrepreneur, journaliste, scénariste et romancier. Il a signé La petite cité dans la prairie (Bords de l\'eau, 2008), Les anges s\'habillent en caillera (Moisson rouge, 2011) et un roman est annoncé pour les prochains jours, Flic Ou Caillera, chez Masque.', 'http://www.histoire-immigration.fr/magazine/2013/3/des-chiffres-et-des-litres', 44, 'Mustapha Harzoune', '2013-3-4', 'polar; banlieue; immigration; Rachid Santaki', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '<i>Le rêve brisé des working girls</i>: un livre sans complaisance', 'Est-il normal, alors que vos camarades vous appelaient naguère « Miss HEC » - tant vous étiez jolie, brillante, ambitieuse - de vous retrouver mère au foyer, quinze ans après le diplôme, pour avoir suivi votre mari à Londres sans avoir pu assurer vos arrières sur le plan professionnel ? ', 'http://femmes.blogs.challenges.fr/archive/2013/03/06/le-reve-brise-des-working-girls-un-livre-sans-complaisance.html', 45, 'Anne-Marie Rocco', '2013-3-6', 'société; place de la femme; Claire Leost', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Doris Lessing revient au roman d’anticipation', '<i>L’Histoire du général Dann</i> est la suite de <i>Mara et Dann</i> paru en 2001. Après un cataclysme climatique, Dann doit survivre à la mort de sa sœur', 'http://www.letemps.ch/Page/Uuid/8ddeeb82-828c-11e2-85ca-819c88a4405e%7C0#.UTm7Khy7mSo', 46, 'André Clavel', '2013-3-2', 'roman; lucidité; monde apocalyptique; Doris Lessing', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L\'élégance du hérisson de Muriel Barbery, la lecture de Jade', 'Jade, étudiante en Journalisme, est passionnée de Littérature. Toutes les semaines, elle publie pour meltyCampus ses dernières lectures. Aujourd\'hui : L\'élégance du hérisson, de Muriel Barbery.', 'http://www.meltycampus.fr/les-lectures-de-jade-l-elegance-du-herisson-muriel-barbery-a158814.html', 47, 'Jade', '2013-2-20', 'avis; livre; Muriel Barbery; Critique', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Changer d\'avis', 'Un recueil d\'essais fulgurants, où la romancière parle librement de son père, de littérature, de Kafka ou de Wallace... avec un humour caustique et irrésistible.', 'http://www.telerama.fr/livres/changer-d-avis,93635.php', 5, 'Marine Landrot', '2013-2-23', 'avis; livre; Zadie Smith; Critique', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Académie française : pourquoi pas Amélie Nothomb sous la Coupole ?', 'L\'Académie française se réunit jeudi 21 février pour tenter de trouver un successeur à Jean Dutourd, dont le fauteuil est vacant. Ce n\'est pas le seul: sur quarante sièges, cinq sont à pourvoir.', 'http://blog.francetvinfo.fr/livres-actualite/2013/02/20/academie-francaise-pourquoi-pas-amelie-nothomb-sous-la-coupole.html', 4, 'Anne Brigaudeau', '2013-2-20', 'Académie française; Amélie Nothomb', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Superman, super-héros du Salon du livre de Paris', 'Le premier des super-héros a 75 ans cette année. Mais l\'Homme d\'Acier ne fête pas ça comme tout le monde. Pour honorer son existence, Zack Snyder le transpose sur grand écran dès cet été dans Superman: Man of Steel. Le Salon du livre de Paris lui rend hommage avec une exposition consacrée aux grandes périodes de sa vie et aux auteurs qui ont su le transformer en légende des comics. Yann Graf, «assistant editor» chez Urban Comics, commente quelques planches et autres illustrations du héros venu de Krypton. Des pièces que l\'on retrouvera pendant l\'exposition Superman Anthologie, tirée elle-même du livre anniversaire.', 'http://www.lefigaro.fr/bd/2013/03/07/03014-20130307DIMFIG00649-superman-super-heros-du-salon-du-livre-de-paris.php', 21, 'Olivier Delcroix', '2013-3-7', 'Superman; Salon du livre', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Amazon grand absent du Salon du livre de Paris', 'En raison des «problèmes d\'image» qu\'il rencontre en France et en Europe, le géant américain a décidé de ne pas prendre de stand au rendez-vous parisien de l\'édition. Une nouvelle regrettable pour les organisateurs de l\'événement.', 'http://www.lefigaro.fr/livres/2013/03/01/03005-20130301ARTFIG00583-amazon-grand-absent-du-salon-du-livre-de-paris.php', 21, 'Fiona Ipert; Mohammed Aissaoui', '2013-3-1', 'Amazon; Salon du livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Amazon absent du Salon du livre de Paris', 'L\'an passé, c\'était l\'événement : pour la première fois dans l\'histoire de la firme, des Salons et autres Foires du livre, Amazon prenait un stand. Un espace de 80 m2 pour promouvoir son Kindle, et exclusivement son Kindle. « Amazon est une réalité, c\'est normal qu\'il soit sur le salon. Ce qui est anormal, c\'est qu\'ils bénéficient d\'un régime fiscal particulier », assurait Antoine Gallimard, alors président du Syndicat national de l\'édition.', 'http://www.actualitte.com/salons/amazon-absent-du-salon-du-livre-de-paris-40244.htm', 35, 'Clément Solym', '2013-2-12', 'Amazon;  Salon du livre de Paris;  Aurélie Filippetti; librairie', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Pour ses 20 ans, Titeuf s\'invite au Salon du livre', 'Zep est un papa comblé : pour les 20 ans de son personnage Titeuf, les éditions Glénat lui offrent une exposition qui se déroulera du 21 mars jusqu\'à la fin du Salon, avec une visite privée, prévue pour 17 h. ', 'http://jeunesse.actualitte.com/salons/pour-ses-20-ans-titeuf-s-invite-au-salon-du-livre-405.htm', 35, 'Fred Ricou', '2013-3-8', 'Zep; Titeuf; 20 ans; salon du livre', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Rouge Tagada », une jolie BD par une madmoiZelle – Interview des auteures', '« Rouge Tagada » est une très jolie BD qui aborde avec délicatesse l’adolescence, l’amitié et l’homosexualité. Elsa a interviewé Stéphanie Rubini, la dessinatrice (une madmoiZelle !) et l’auteure, Charlotte Bousquet !', 'http://www.madmoizelle.com/rouge-tagada-153493', 48, 'Elsa', '2013-3-11', ' Adolescence; amitié: BD, Chronique BD; Homosexualité; Interview', '3', '0', '0');
