INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L\'architecture expliquée aux enfants', 'L\'architecture pourrait sembler pointue pour des enfants mais plusieurs livres sur le sujet existent. A travers ses deux livres publiés chez Hélium, Didier Cornille a fait le pari de s\'adresser à des enfants de 8/9 ans.', 'http://www.franceinfo.fr/education-jeunesse/le-zoom-culture/zoo-916665-2013-03-11', 8, 'Raphaële Botte', '2013-3-11', 'podcast; architecture; jeunesse; enfant', '13', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Souviens-toi de m\'oublier", de Régis Descott', 'Nous aimerions tous, à un moment ou un autre de notre vie, oublier une action misérable, une souffrance ou un bonheur perdu. Mais comme c\'est impossible, on se prend à rêver à une potion magique qui effacerait les mauvais souvenirs. C\'est autour de ce thème que l\'écrivain Régis Descott a construit son nouveau roman étrange et inquiétant.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-11-03-13-regis-descott-916387-2013-03-11', 8, 'Philippe Vallet', '2013-3-11', 'podcast; roman; Régis Descott', '22', '0', '0');
