INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Lecture : "Annabel" de Kathleen Winter', 'Wayne est un garçon. Depuis que ses parents ont décidé de l\'opérer et l\'élever comme tel. Mais Wayne est né hermaphrodite et en lui, Annabel cherche aussi à exister… Fabienne Bussaglia lit un extrait d\'“Annabel” de Kathleen Winter (Bourgois).', 'http://www.telerama.fr/livre/lecture-annabel-de-kathleen-winter,94449.php#xtor=RSS-22', 5, 'NULL', '2013-3-9', 'podcast; lecture; Kathleen Winter', '22', '0', '0');
