INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L’écrivain roumain Cartarescu ne participera au Salon du livre à Paris', 'La Roumanie et Barcelone seront à l’honneur au 33e Salon du livre de Paris, qui accueillera du 22 au 25 mars plus de 2.000 auteurs et 1.200 exposants venant de 45 pays, avec une internationalisation croissante et une place de plus en plus importante accordée au numérique. Environ 190.000 visiteurs s’étaient rendus en 2012 Porte de Versailles.', 'http://www.liberation.fr/livres/2013/03/15/l-ecrivain-roumain-cartarescu-ne-participera-au-salon-du-livre-a-paris_888840', 39, 'NULL', '2013-3-15', 'Salon du livre de Paris; écrivain; Mircea Cartarescu', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Déjà un livre sur François Ier, le nouveau pape', 'La blanche fumée annonciatrice d\'une Bonne Nouvelle, s\'est à peine échappée du Conclave, apportant avec elle l\'identité du nouveau souverain pontife qu\'un livre est prêt, portant sur celui qu\'il faudra désormais appeler Pape François. C\'est l\'éditeur américain Random House qui a prévu le coup, à travers sa collection de livres traitant du catholicisme, Image Books. Gloire à Dieux, qui fait si bien les choses...', 'http://www.actualitte.com/les-maisons/deja-un-livre-sur-francois-ier-le-nouveau-pape-41023.htm', 35, 'Xavier S. Thomann', '2013-3-16', 'Pape François; Random House; Image books; Catholicisme', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Bande dessinée - Une couverture de Peyo atteint les 80.000 euros lors d\'une vente aux enchères à Bruxelles', 'Une illustration à l\'encre de Chine réalisée par Peyo pour la couverture de l\'album de Johan et Pirlouit "Le Lutin du bois aux roches" a été attribuée à 65.000 euros, soit 79.302 euros avec les frais, lors de la 28ème vente aux enchères organisée à Bruxelles par Banque dessinée. La pièce était estimée entre 70 et 80.000 euros.', 'http://www.rtbf.be/culture/exposition/detail_bande-dessinee-une-couverture-de-peyo-atteint-les-80-000-euros-lors-d-une-vente-aux-encheres-a-bruxelles?id=7949490', 50, 'Belga', '2013-3-17', 'Vente aux enchères; BD; bande dessinée; dessins; Hergé; Peyo', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Les mystérieuses cités d\'or" adapté en bande-dessinée', 'Série culte franco-japonaise des années 1980, “Les mystérieuses cités d’or” fait une arrivée inédite sur papier cette semaine, avec une adaptation en manga réalisée par un auteur français. Le mangaka Thomas Bouveret a ainsi choisi de s’intéresser à la première saison du dessin animé qui retrace les aventures de trois enfants, Esteban, Zia et Tao, dans l’Amérique latine du XVIe siècle à la recherche de traces de civilisations disparues.', 'http://teleobs.nouvelobs.com/articles/41251-les-mysterieuses-cites-d-or-adapte-en-bande-dessinee', 10, 'NULL', '2013-3-18', 'Les mystérieuses cités d\'or; manga; bande dessinée; BD', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Les Mystérieuses Cités d\'Or saison 2: "Gnangnan, infantilisée à l\'extrême et complètement aseptisée"', 'TF1 diffusera à partir du 7 avril la deuxième saison des Mystérieuses Cités d\'Or, 30 ans après l\'arrêt du dessin animé culte. Mais pour notre contributrice, la version revisitée a dépouillé la série de tout ce qui faisait son succès. Coup de gueule!', 'http://www.lexpress.fr/culture/tele/les-mysterieuses-cites-d-or-saison-2-gnangnan-infantilisee-a-l-extreme-et-completement-aseptisee_1232817.html', 6, ' vaness12', '2013-3-19', 'Les mystérieuses cités d\'or; manga; bande dessinée; BD; TF1', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Les mystérieuses cités d’or : un retour plébiscité par les enfants', 'Le dimanche 9 décembre dernier, TF1 lançait la seconde saison des Mystérieuses cités d’or près de trente ans après la disparition du dessin animé. Diffusé deux fois dans « TFOU », ce double épisode a réalisé une excellente performance, de très bon augure pour la suite prévue pour avril 2013.', 'http://www.toutelatele.com/les-mysterieuses-cites-d-or-un-retour-plebiscite-par-les-enfants-47796', 51, 'Robin Girard-Kromas', '2013-3-14', 'Les mystérieuses cités d\'or; manga; bande dessinée; BD; TF1', '3', '0', '0');
