INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Branle-bas de combat chez les bédénautes', 'C\'est une fièvre éditoriale à laquelle le petit monde de la bande dessinée n\'est pas habitué. Pas moins de quatre revues numériques consacrées au 9e art investissent la Toile en cette année 2013. Trois existent déjà, la quatrième sera lancée en septembre.', 'http://www.lemonde.fr/livres/article/2013/03/13/branle-bas-de-combat-chez-les-bedenautes_1847248_3260.html', 13, 'Frédéric Potet', '2013-3-13', 'Professeur Cyclope; La Revue dessinée; Mauvais esprit; BD Nag; BD; bande dessinée', '3', '0', '0');
