INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Les libraires se mobilisent à la veille du Salon du livre', 'Le groupement de libraires « Initiales » s’inquiète de la concurrence du géant Amazon et réclame les mêmes conditions commerciales.', 'http://www.la-croix.com/Culture/Livres/Livres/Les-libraires-se-mobilisent-a-la-veille-du-Salon-du-livre-_NG_-2013-03-19-922622', 53, 'Sabine Audrerie', '2013-3-19', 'Initiales; Salon du livre; Amazon; marché du livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Seuls », de Wajdi Mouawad, un livre de textes et peintures, un spectacle actuellement au théâtre de Chaillot', 'Wajdi Mouawad est un magicien, qui, comme le funambule sur un fil qu’il représente durant quelques secondes gracieuses en scène, nous emmène à l’endroit où le destin bascule et où l’on ne sait plus si c’est du côté de la vie, de la mort, du cauchemar ou de la réalité.', 'http://blogs.lexpress.fr/les-8-plumes/2013/03/21/seuls-de-wajdi-mouawad-un-livre-de-textes-et-peintures-un-spectacle-actuellement-au-theatre-de-chaillot/', 6, 'Véronique Poirson', '2013-3-21', 'Seuls; Théâtre de Chaillot; Wajdi Mouawad; théâtre', '27', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Salon du livre : le grand virage numérique', 'Nouveau contrat d\'édition, lutte contre le piratage, numérisation des œuvres indisponibles : le monde du livre met les bouchées doubles pour s\'adapter à l\'ère du numérique, défendant par ailleurs un plan de soutien à la librairie, à la veille du Salon de Paris, du 22 au 25 mars.', 'http://www.francetv.fr/culturebox/salon-du-livre-le-grand-virage-numerique-133769', 34, 'NULL', '2013-3-20', 'Salon du livre de Paris; livre numérique', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le monde du livre en plein virage numérique', 'Nouveau contrat d\'édition, lutte contre le piratage, numérisation des oeuvres indisponibles...: le monde du livre met les bouchées doubles pour s\'adapter à l\'ère du numérique, défendant par ailleurs un plan de soutien à la librairie, à la veille du Salon de Paris, du 22 au 25 mars.', 'http://www.leparisien.fr/flash-actualite-culture/le-monde-du-livre-en-plein-virage-numerique-20-03-2013-2656167.php', 37, 'NULL', '2013-3-20', 'Salon du livre de Paris; livre numérique', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Titeuf, Superman, Spirou, héros du Salon du livre 2013', 'Depuis 15 ans, le Salon du livre accorde une place de choix à la bande dessinée et propose cette année un large panel d\'animations, d\'expositions ou de rencontres. Panorama de cette édition 2013.', 'http://www.lefigaro.fr/bd/2013/03/21/03014-20130321ARTFIG00498-titeuf-superman-spirou-heros-du-salon-du-livre-2013.php', 21, 'Aurélia Vertaldi', '2013-3-21', 'Titeuf; Superman; Spirou; Salon du livre de Paris; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livre : l\'Etat au chevet des librairies', 'A l\'occasion du Salon du livre, Aurélie Filippetti va annoncer les premières mesures de soutien. Elles visent la librairie indépendante.', 'http://www.lesechos.fr/entreprises-secteurs/medias/actu/0202656134271-livre-l-etat-au-chevet-des-librairies-551484.php', 54, 'Gregoire Poussielgue', '2013-3-22', 'Salon du livre de Paris; librairie; Aurélie Filippetti', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Harlan Coben à Quais du polar', 'L’auteur des aventures de Myron Bolitar donnera le coup d’envoi du match Lyon-Sochaux pendant le festival Quais du polar.', 'http://www.livreshebdo.fr/actualites/medias---manifestations/actualites/harlan-coben-a-quais-du-polar-/10323.aspx', 7, 'cc', '2013-3-21', 'Quais du polar; Harlan Coben; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La bataille du livre numérique commence enfin en France', 'Alors que s\'ouvre ce vendredi jusqu\'à lundi le Salon du Livre Porte de Versailles, la concurrence du livre numérique monopolise toutes les attentions. Alors que ce segment n\'a représenté qu\'une part très négligeable du marché français du livre en 2012, les professionnels, notamment les libraires, sont inquiets. Alexandre Bompard, patron de la Fnac, appelle pourtant les éditeurs à ne pas négliger ce circuit de distribution.', 'http://www.latribune.fr/technos-medias/medias/20130322trib000755572/la-bataille-du-livre-numerique-commence-enfin-en-france.html', 55, 'Nabil Bourassi', '2013-3-22', 'Salon du Livre de Paris; livre numérique; Alexandre Bompard; FNAC', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Portrait-robot du lecteur d\'e-books', 'A l\'occasion du Salon du Livre, deux enquêtes révèlent que le lecteur numérique est un bobo trentenaire. Les détails sont intéressants quand même. ', 'http://bibliobs.nouvelobs.com/numerique/20130321.OBS2657/portrait-robot-du-lecteur-d-e-books.html?xtor=RSS-15', 18, 'Louis Blanchard', '2013-3-21', 'livre numérique; e-book;', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livre : La chronique de David Medioni – Le banquier anarchiste', 'Cette semaine notre chroniqueur-journaliste David Medioni vous a sélectionné un livre datant de 1922 qui malgré tout colle parfaitement à l’actualité.', 'http://www.lesdeblogueurs.tv/culture/livre-la-chronique-de-david-medioni-le-banquier-anarchiste/', 23, 'David Medioni', '2013-3-23', 'chronique; David Medioni; Fernando Pessoa; roman; critique', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Wajdi Mouawad à Chaillot : Seuls et multiple', 'Wajdi Mouawad reprend à Chaillot son spectacle « Seuls » créé en 2008. Au centre d’une œuvre composite faisant autant appel à une écriture textuelle forte qu’aux éléments plastiques et performatifs que sont la peinture et la vidéo, l’auteur, metteur en scène et comédien livre une parole monologuée très personnelle emprunte d’interrogations identitaires ou se tissent les fils de la mémoire et de l’origine transcendées et résolues par la création artistique.', 'http://toutelaculture.com/2013/03/a-chaillot-wajdi-mouawad-est-seul-et-multiple/', 56, 'Christophe Candoni', '2013-3-21', 'Seuls; Théâtre de Chaillot; Wajdi Mouawad; théâtre', '27', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Seuls » écrit et mise en scène par Wajdi Mouawad, à Paris', 'Pièce singulière dans le parcours de Wajdi Mouawad habitué à de vastes épopées ((Incendies, Littoral, Forêts, Ciels que nous avions chroniquées sur Publik’Art) où s’entremêlent des destins initiatiques et tourmentés incarnés par de nombreux personnages, « Seuls » est un solo interprété par l’auteur himself.', 'http://publikart.net/seuls-de-et-mise-en-scene-par-wajdi-mouawad-a-paris', 57, 'Amaury', '2013-3-23', 'Seuls; Théâtre de Chaillot; Wajdi Mouawad; théâtre', '27', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti lance un plan d\'aide aux librairies indépendantes', 'La ministre de la culture, Aurélie Filippetti, a lancé lundi 25 mars au Salon du livre un plan d\'aide à la librairie indépendante, en annonçant l\'injection rapide de 9 millions d\'euros destinés aux libraires, un futur fonds de soutien et la prochaine nomination d\'un médiateur du livre.', 'http://www.lemonde.fr/livres/article/2013/03/25/aurelie-filippetti-lance-un-plan-d-aide-aux-librairies-independantes_1854052_3260.html', 13, 'NULL', '2013-3-25', 'Aurélie Filippetti; plan d\'aide; librairie indépendante; Salon du livre de Paris', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Un plan d\'aide de 9 millions d\'euros pour les libraires indépendants', 'La ministre de la Culture Aurélie Filipetti a annoncé la mise en place d\'un fonds d\'avance de trésorerie destiné aux libraires, ainsi qu\'un renforcement de l\'aide à la transmision des librairies. Un médiateur du livre devrait par ailleurs être nommé avant l\'été.', 'http://lentreprise.lexpress.fr/gestion-entreprise/un-plan-d-aide-de-9-millions-d-euros-pour-les-libraires-independants_39433.html', 58, 'NULL', '2013-3-25', 'Aurélie Filippetti; plan d\'aide; librairie indépendante; Salon du livre de Paris', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Neuf millions d\'euros pour soutenir la librairie', 'Aurélie Filippetti a annoncé la mise en place d\'un fonds d\'urgence de soutien pour soulager les problèmes de trésorerie dans le secteur et faciliter la transmission des commerces.', 'http://www.lefigaro.fr/medias/2013/03/25/20004-20130325ARTFIG00664-le-gouvernement-debloque-9millions-d-euros-pour-soutenir-la-librairie.php', 21, 'Alexandre Debouté', '2013-3-26', 'Aurélie Filippetti; plan d\'aide; librairie indépendante; Salon du livre de Paris', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La mort de Jean-Marc Roberts, patron des éditions Stock', 'Jean-Marc Roberts, patron des éditions Stock, est décédé lundi en fin de matinée des suites d\'un cancer à l\'âge de 58 ans, a annoncé sa maison d\'édition. L\'éditeur, écrivain prolixe, venait de publier un ouvrage sur sa maladie, "Deux vies valent mieux qu\'une". Il dirigeait aussi la célèbre collection Bleue où est paru récemment le tapageur "Belle et Bête" de Marcela Iacub.', 'http://www.francetv.fr/culturebox/la-mort-de-jean-marc-roberts-patron-des-editions-stock-134013', 34, 'NULL', '2013-3-25', 'Jean-Marc Roberts; éditions Stock', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Salon du Livre : l\'édition en région, un combat quotidien !', 'L\'édition régionale était bien représentée au 33e Salon du Livre : 16 grands stands régionaux et de nombreux indépendants. Un secteur dynamique mais qui, lui aussi, souffre de la crise.', 'http://www.francetv.fr/culturebox/salon-du-livre-ledition-en-region-un-combat-quotidien-133993', 34, 'Pierre-Yves Grenu', '2013-3-25', 'Salon du livre de Paris; éditeur régional', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Aurélie Filippetti débloque 9 millions d\'euros d\'aide aux libraires', 'Aurélie Filippetti a annoncé lundi la mise en place d\'un médiateur du livre ainsi que l\'instauration d\'un fonds d\'avance de trésorerie de 5 millions d\'euros dédié aux libraires et un renforcement de l\'aide à la transmission des commerces de 4 millions afin de "protéger" ce secteur fragilisé.', 'http://www.francetv.fr/culturebox/aurelie-filippetti-debloque-9-millions-deuros-daide-aux-libraires-133983', 34, 'Jacky Bornet', '2013-3-25', 'Aurélie Filippetti; plan d\'aide; librairie indépendante; Salon du livre de Paris', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Les impunis. Cambodge, un voyage dans la banalité du Mal", d\'Olivier Weber', 'Entre 1975 et 1979, les Khmers rouges, qui se réclamaient du communisme, ont assassiné deux millions de cambodgiens. Après ce génocide, pas de procès, ou presque. L\'écrivain et grand reporter Olivier Weber est allé à la rencontre de ces anciens bourreaux réfugiés dans la zone nord du Cambodge. Là, ils ont créé une sorte dans de petit Etat voyou. Ils n\'ont pas de remords. Ils vivent de trafics, roulent sur l\'or et parlent de littérature. Un récit exceptionnel, troublant et surréaliste.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-les-impunis-930149-2013-03-25', 8, 'Philippe Vallet', '2013-3-25', 'Olivier Weber; Cambodge; Khmers rouges; Histoire', '9', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Un sentiment plus fort que la peur" de Marc Levy', 'Le 33e Salon du Livre de Paris, qui se tient Porte de Versailles en partenariat avec Radio France, permet au public de retrouver les stars de l\'édition. Parmi celles-ci, Marc Levy dont le nouveau roman caracole déjà en tête des meilleures ventes.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-24-03-929461-2013-03-24', 8, 'Philippe Vallet', '2013-3-24', 'Marc Lévy; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"La bicyclette statique", traduit du catalan par Edmond Raillard', 'Le 33è Salon du Livre de Paris, qui se tient en partenariat avec Radio France, fête cette année les écrivains de Barcelone. ', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-23-03-13-928569-2013-03-23', 8, 'Philippe Vallet', '2013-3-23', 'Edmond Raillard; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Nina Simone, roman", de Gilles Leroy', 'Nina Simone est une des plus grandes musiciennes de jazz. C\'est la vie de cette diva, qui ressemble à un roman, que raconte Gilles Leroy, Prix Goncourt, dans son nouveau livre. Le portrait émouvant d\'une femme blessée.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-22-03-gilles-leroy-nina-simone-roman-927349-2013-03-22', 8, 'Philippe Vallet', '2013-3-22', 'Nina Simone; Gilles Leroy; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Salon du livre : j\'aime les ouvrages de cuisine, mais attention aux pièges', 'LE PLUS. Le livre de cuisine fait son entrée cette année au Salon du livre. Du sushi, au macaron, en passant par le burger, les publications ont doublé entre 2003 et 2011. Alors que penser de ces livres ? Sont-ils toujours fiables ? Camille, blogeuse et grande lectrice, nous fait partager son avis et avoue avoir eu certaines surprises.', 'http://leplus.nouvelobs.com/contribution/804991-salon-du-livre-pourquoi-certains-livres-de-cuisine-ne-sont-pas-fiables.html', 59, 'Camille P', '2013-3-25', 'Salon du livre de Paris; Cuisine; livre culinaire', '6', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Au Salon du Livre, le succès des ouvrages de cuisine pour enfants', 'Les ouvrages de cuisine pour enfants, de plus en plus nombreux, ont investi le Salon du livre de Paris qui réserve pour la première fois tout un espace à la gastronomie, où les plus jeunes peuvent apprendre à savourer betteraves, panais et même navets.', 'http://tempsreel.nouvelobs.com/culture/20130324.AFP7620/au-salon-du-livre-le-succes-des-ouvrages-de-cuisine-pour-enfants.html', 10, 'NULL', '2013-3-24', 'Salon du livre de Paris; enfants; gastronomie; cuisine', '6', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livre numérique : Yannick Monget ne regrette rien', 'L\'auteur lorrain Yannick Monget était en dédicace au Salon du Livre de Paris (23-25 mars 2013) pour son roman  "Résilience". Un récit publié uniquement en numérique et pourtant, il s\'agit bien d\'un vrai livre avec de vrais lecteurs !', 'http://lorraine.france3.fr/2013/03/24/livre-numerique-yannick-monget-ne-regrette-rien-222341.html', 60, 'Hélène Messang', '2013-3-24', 'Yannick Monget; livre numérique; SF; Science-Fiction', '25', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Salon du livre 2013 : de nombreux visiteurs mais peu d\'acheteurs', 'Selon Livres Hebdo, la fréquentation au salon du livre a été bonne ce week-end, même si les visiteurs ont moins acheté cette année. Succès en revanche pour le Square culinaire, la nouveauté 2013, qui proposait pour la première fois un regroupement des livres de bouche.', 'http://www.francetv.fr/culturebox/salon-du-livre-2013-de-nombreux-visiteurs-mais-peu-dacheteurs-133987', 34, 'Laurence Houot', '2013-3-25', 'Salon du livre de Paris; marché du livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Salon du livre : beaucoup de visiteurs, peu d\'acheteurs', 'Le Salon du livre de Paris a fermé ses portes ce lundi soir. 190.000 personnes se sont rendues à la Porte de Versailles. C\'est un peu mieux que l\'an passé', 'http://www.lefigaro.fr/livres/2013/03/25/03005-20130325ARTFIG00665-salon-du-livre-beaucoup-de-visiteurs-peu-d-acheteurs.php', 21, 'Mohammed Aissaoui', '2013-3-25', 'Salon du livre de Paris; marché du livre', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Last Man, une piste graphique pertinente à explorer avec Vivès, Balak et Sanlaville', 'Une très élégante ouverture graphique et pertinente de trois auteurs qui la joue façon équipe et sortent un premier tome inclassable. Balak, Michaël Sanlaville et l’incontournable Bastien Vivès ont uni leurs talents et leurs idées. ', 'http://www.ligneclaire.info/last-man-une-piste-graphique-pertinente-a-explorer-avec-vives-balak-et-sanlaville-3679.html', 61, 'Jean Laurent Truc', '2013-3-7', 'Lastman; Mickaël Sanlaville; Balak; Bastien Vivès; BD; Bande dessinée', '3', '0', '0');
