INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '« Ekhö », la géniale nouvelle BD d’Arleston – Interview', '« Ekhö » est la géniale nouvelle BD d’Arleston, un des scénaristes les plus prolifiques de ces dernières décennies. Elsa a pu l’interviewer au Salon du Livre 2013 !', 'http://www.madmoizelle.com/ekho-arleston-interview-156652', 48, 'Elsa', '2013-3-26', 'Christophe Arleston; Alessandro Barbucci; BD; bande dessinée', '3', '0', '0');
