INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Coulisses de Canal+ : le livre choc d\'un ex-chroniqueur du "Grand Journal", Ollivier Pourriol', 'Confronté à une crise d\'image depuis plusieurs mois, "Le Grand Journal" ne sortira probablement pas grandi de cette incursion dans ses coulisses. "On/Off", c\'est l\'histoire d\'un recrutement raté. Celle d\'un romancier, un peu naïf, tombé dans le grand chaudron de la télé.', 'http://www.ozap.com/actu/coulisses-de-canal-le-livre-choc-d-un-ex-chroniqueur-du-grand-journal-ollivier-pourriol/446527', 85, 'Julien Bellver', '2013-4-10', 'Ollivier Pourriol; Canal+; polémique', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'BD : « Tale of Sand », mais qui tire les ficelles ?', 'De ce côté de l’Atlantique, le nom de Jim Henson n’est peut-être pas des plus familiers. Sa principale création, en revanche, est universelle. C’est en 1976 que le Muppet Show voit le jour, une série en cinq saisons qui a connu un succès considérable.', 'http://blogs.rue89.com/le-coin-du-libraire/2013/04/10/bd-tale-sand-mais-qui-tire-les-ficelles-230051', 3, 'Anthony Boyer', '2013-4-10', 'Tale of Sand; Muppet Show; Jim Henson; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Blackface Banjo, la "Swing Touch" de Frantz Duchazeau', 'À travers un personnage de musicien ambulant, Frantz Duchazeau pose un nouveau portrait en creux de l’Amérique. Avec « Blackface Banjo », il se montre toujours aussi inspiré par la musique et son folklore.', 'http://www.actuabd.com/Blackface-Banjo-la-Swing-Touch-de', 2, 'Morgan Di Salvia', '2013-4-6', 'Blackface Banjo; BD; bande dessinée; Frantz Duchazeau', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Polémique : le producteur du "Grand Journal" répond à Ollivier Pourriol', 'L\'ancien chroniqueur raconte les coulisses de l\'émission star de Canal+ dans un livre à paraître intitulé "On/off".', 'http://www.lepoint.fr/chroniqueurs-du-point/emmanuel-berretta/polemique-le-producteur-du-grand-journal-repond-a-ollivier-pourriol-12-04-2013-1654127_52.php', 15, 'Emmanuel Berreta', '2013-4-12', 'Ollivier Pourriol; Canal+; polémique', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'L’autoédition surfera sur la vague du livre numérique', 'Le livre numérique (ebook) représentait 28% des ventes de livres aux USA en novembre 2012. En France nous peinons à atteindre… 1%. Autant dire que la marge de progression est conséquente.', 'http://www.journaldunet.com/media/expert/53936/l-autoedition-surfera-sur-la-vague-du-livre-numerique.shtml', 86, 'Jean-Yves Normant', '2013-4-11', ' Jean-Yves Normant; auto-édition; livre numérique; ebook', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Cercle Polar #118 reçoit le romancier John Burdett', 'Un "Cercle Polar" exceptionnel, enregistré fin mars pendant le festival Quais du Polar à Lyon. Une heure avec John Burdett, écrivain britannique qui partage sa vie entre Bangkok et le sud de la France. L’Asie, sa prostitution, ses mafieux, sa police et ses héros, vus par un écrivain politiquement incorrect. Une rencontre animée par Christine Ferniot et Michel Abescat.', 'http://www.telerama.fr/livre/cercle-polar-118-re-oit-le-romancier-john-burdett,96070.php', 5, 'Christine Ferniot', '2013-4-13', 'Cercle polar; John Burdett', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Les e-books représentent 23% du marché du livre aux Etats-Unis', 'Les derniers chiffres des éditeurs américains confirment l\'implantation de la lecture numérique, même si les ventes d\'e-books ont moins progressé en 2012 qu\'en 2011.', 'http://lexpansion.lexpress.fr/high-tech/les-e-books-representent-23-du-marche-du-livre-aux-etats-unis_380022.html', 87, 'NULL', '2013-4-12', 'ebook; livre numérique; Etats-Unis', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Chronique BD. Le Magicien d\'Oz arrive chez Marvel !', 'Le magicien d’Oz fait parti des grands classiques de la littérature mondiale. Il a permis à des millions de lecteurs de parcourir avec la petite Dorothée et ses amis les mondes merveilleux du pays d’Oz.Après plusieurs adapatations cinématographiques, voici la bd.', 'http://www.programme-tv.net/news/tv/39209-chronique-bd-le-magicien-oz-arrive-chez-marvel/', 71, 'Jean-Philippe Lefèvre', '2013-4-12', 'Le Magicien d\'Oz; Lyman-Frank Baum; BD; bande dessinée', '3', '0', '0');
