INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La vie de Coco Chanel racontée en BD', 'Pascale Frey, journaliste de mode pour le magazine Elle, et le dessinateur Bernard Ciccolini sortent un livre sur la vie de la créatrice, de son enfance à la fondation de sa maison de couture, aux éditions Naïve. ', 'http://www.lexpress.fr/styles/mode/la-vie-de-coco-chanel-racontee-en-bd_1239830.html', 6, 'Julie Lacourt', '2013-4-12', 'Coco Chanel; BD; bande dessinée; mode', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Lecture : “Les eaux tumultueuses” d\'Aharon Appelfeld', 'A la fin des années 30, une poignée de célibataires, juifs pour la plupart, mène une vie de bohème dans une pension viennoise. Fabienne Bussaglia nous lit un extrait des “Eaux tumultueuses" d\'Aharon Appelfeld.', 'http://www.telerama.fr/livre/lecture-les-eaux-tumultueuses-d-aharon-appelfeld,96067.php#xtor=RSS-36', 5, 'Fabienne Bussaglia', '2013-4-14', 'Les eaux tumultueuses; Aharon Appelfeld; roman', '22', '0', '0');
