INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'A Aix-en-Provence, une expo et une BD consacrées à "L\'Etranger" de Camus', 'Jusqu\'au 27 avril, la Cité du Livre d\'Aix-en-Provence présente une exposition autour d\'un chef-d’œuvre de la littérature française, "L\'Etranger". Un jeu de regards entre les documents d’archives issus des fonds du Centre Albert Camus et la sélection de planches originales du niçois Jacques Ferrandez, auteur d\'une bande dessinée adaptée du roman.', 'http://www.francetv.fr/culturebox/a-aix-en-provence-une-expo-et-une-bd-consacrees-a-letranger-de-camus-134949', 34, 'Céline Pauilhac', '2013-4-16', 'Albert Camus; l\'étranger; BD; bande dessinée', '3', '0', '0');
