INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Olivier Norek, Toulousain, policier et... auteur de polar, publie "Code 93"', '"Code 93" sort ce jeudi en librairie. Premier roman d\'un Toulousain, policier depuis 15 ans en Seine-Saint-Denis, qu\'il a en grande partie écrit dans la maison familiale en Aveyron. Rencontre avec un romancier-policier qui veut réconcilier les lecteurs avec les "vrais flics".', 'http://midi-pyrenees.france3.fr/2013/04/17/olivier-norek-toulousain-policier-et-auteur-de-polar-236395.html', 89, 'Fabrice Valery', '2013-4-17', 'Olivier Norek; policier; auteur; polar', '21', '0', '0');
