INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Pierre Tombal : 29. Des os et des bas', 'Voilà trente ans déjà que Cauvin, Hardy et la mort jouent à s’amuser, à nous amuser, tout au long d’une série populaire qui, si elle prend la vie au sérieux, aime à rire de tout… Et Pierre Tombal s’expose au Centre Belge de la Bande Dessinée.', 'http://www.rtbf.be/culture/chronique/detail_pierre-tombal-29-des-os-et-des-bas-jacques-schrauwen?id=7971982&chroniqueurId=7754767', 83, 'Jacques Schraûwen', '2013-4-14', 'Raoul Cauvin; Marc Hardy; BD; bande dessinée', '3', '0', '0');
