INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Road tripes" : un Frenchy qui lâche les chevaux', 'Voici le Sebastien Loeb du roman noir, expert en dérapages contrôlés, flirtant avec les sorties de route, tuyoyant les abîmes du grand n\'importe quoi, mais maîtrisant la trajectoire sinueuse et pied au plancher de son récit. ', 'http://planete-polars.blog.leparisien.fr/archive/2013/04/17/road-tripes-un-frenchy-qui-lache-les-chevaux.html', 37, 'Philippe Lemaire', '2013-4-17', 'Sébastien Gendron; Road Tripes; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"La fille" : la BD musicale de Christophe Blain et Barbara Carlotti', 'Après avoir publié son dernier album "L\'Amour, l\'Argent, le Vent" l\'année dernière, Barbara Carlotti se lance dans une nouvelle aventure. Avec l\'aide de l\'auteur de bandes dessinées Christophe Blain, elle propose une BD musicale intitulée "La fille". Découvrez les premières images !', 'http://www.chartsinfrance.net/Barbara-Carlotti/news-85089.html', 90, 'Laurent Sandres', '2013-4-20', 'La fille; Christophe Blain; Barbara Carlotti; BD; bande dessinée', '3', '0', '0');
