INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Lecture : “Lionel Asbo, l\'état de l\'Angleterre” de Martin Amis', ' A travers les aventures ­pathétiques et burlesques de Lionel et des siens, un portrait féroce de la classe moyenne anglaise du début du XXIe siècle. Fabienne Bussaglia nous lit un extrait du dernier roman de Martin Amis : “Lionel Asbo, l\'état de l\'Angleterre”, publié chez Gallimard.', 'http://www.telerama.fr/livre/lecture-lionel-asbo-l-etat-de-l-angleterre-de-martin-amis,96886.php#xtor=RSS-36', 5, 'Fabienne Bussaglia', '2013-5-4', 'Martin Amis; Angleterre; Londres; roman', '22', '0', '0');
