INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Vautrin vu par la BD', 'ean Vautrin, prix Goncourt 1989, va voir ses romans noirs des années 70 et 80 adaptés en bande dessinée. Baru tire le premier avec "Canicule". Tardi, Moynot, Pourquié suivront.', 'http://www.franceinfo.fr/livre/bd-bande-dessinee/vautrin-vu-par-973637-2013-05-05', 8, 'Jean Christophe Ogier', '2013-5-5', 'Baru; Casterman; Vautrin; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'David Garcia: « L\'environnement à l\'OM n\'a pas été assaini »', 'Après le journal L’Équipe, David Garcia s\'attaque à une autre institution du sport français : l\'OM. Le journaliste a enquêté pendant deux ans sur le club marseillais. Et il ne s\'est pas fait que des amis. À commencer par José Anigo.', 'http://www.sofoot.com/david-garcia-l-environnement-a-l-om-n-a-pas-ete-assaini-169218.html', 97, 'Mario Durante', '2013-5-4', 'Football; OM; Olympique de Marseille; David Garcia; mafia; journalisme; interview', '26', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le dernier livre de Gisèle Pineau ne laisse pas indifférent', 'Entre l\'auteur Gisèle Pineau, et l\'assistance, un véritable dialogue s\'est installé, alimenté par des confidences et des partages d\'opinion. Cent vies et des poussières, le dernier roman de l\'écrivain n\'a laissé personne indifférent.', 'http://www.guadeloupe.franceantilles.fr/actualite/culture-et-patrimoine/le-dernier-livre-de-gisele-pineau-ne-laisse-pas-indifferent-215821.php', 98, 'Déborah Mathey', '2013-5-5', 'Gisèle Pineau; rencontre; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Je te vois reine des quatre parties du monde" d\'Alexandra Lapierre', 'Isabel Barreto est l\'une des plus extraordinaires aventurières de l\'histoire. Seule femme amiral de la navigation, elle a affronté les conquistadors et les routes maritimes inconnues.', 'http://www.franceinfo.fr/livre/le-livre-du-jour/le-livre-du-jour-31-03-isabel-barreto-936265-2013-03-31', 8, 'Philippe Vallet', '2013-3-31', 'Philippe Vallet; Alexandra Lapierre; Flammarion; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Thierry Séchan s\'adresse à Renaud dans son livre "Lettres à mon frère"', 'Très proche par le passé, le chanteur Renaud et son frère sont désormais brouillés. Régulièrement accusé de profiter de la célébrité de son cadet, Thierry Séchan sort un septième livre sur ce frère qu\'il admire depuis longtemps. BFMTV l\'a rencontré.', 'http://www.bfmtv.com/divertissement/thierry-sechan-sadresse-a-renaud-livre-lettres-a-frere-508730.html', 99, 'Claire Fleury ', '2013-5-6', 'Thierry Séchan; Renaud; chanson; rencontre', '5', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Qui ?", le roman policier du printemps signé Jacques Expert', 'Le roman policier "Qui?" de Jacques Expert chez Sonatine est un thriller efficace et scotchant. Une bonne idée de lecture pour le printemps.', 'http://www.rtl.fr/emission/laissez-vous-tenter/billet/qui-le-roman-policier-du-printemps-signe-jacques-expert-7761159875', 69, 'Bernard Lehut', '2013-5-7', 'Lecture; Jacques Expert; policier; polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le retour (littéraire) de Ségolène Royal', 'La socialiste sort un livre de portraits, qu’Europe 1 a pu consulter en avant-première.', 'http://www.europe1.fr/Politique/Le-retour-litteraire-de-Segolene-Royal-1509477/', 38, 'Camille Langlade et Gaétan Supertino', '2013-5-8', 'Ségolène Royal; essai', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'WW2.2. - Tome 4 - Eliminer Vassili Zaitsev', 'WW2.2 n’est pas le titre d’une série de bande dessinée sur la seconde guerre mondiale version nouvelle technologie. C’est plutôt le conflit revisité, imaginé si ... Hitler était mort en 1939, que les russes soient les alliés des allemands, que les États-Unis s’allient avec le Japon, que les français n’aient pas capitulé et que les anglais doivent faire face à une invasion germano-soviétique sur leur île !', 'http://bd.krinein.com/ww22-4-eliminer-vassili-zaitsev-26108/critique-20895.html', 43, 'plienard', '2013-5-8', 'WW2.2; guerre; seconde guerre mondiale; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Royal revient avec un livre sur ses mentors', 'Peu présente dans les médias ces dernières semaines, la présidente de la BPI publie un livre de portraits, ceux des 15 personnalités qui l\'ont inspirée.', 'http://www.lepoint.fr/politique/royal-revient-avec-un-livre-sur-ses-mentors-08-05-2013-1664846_20.php', 15, 'SIPA MEDIA', '2013-5-8', 'Ségolène Royal; essai', '8', '0', '0');
