INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Bastien Vivès, nouveau prince de la bande dessinée', 'Le prolifique et marginal Bastien Vivès s\'est lancé dans un vaste projet de manga, Lastman. Capable de jouer sur tous les terrains, le dessinateur de 29 ans, qui a conquis critiques et lecteurs, tente d\'expliquer les raisons de son succès.', 'http://jeanmorizot.blog.lemonde.fr/2013/05/10/bastien-vives-nouveau-prince-de-la-bande-dessinee/', 13, 'Jean Morizot', '2013-5-10', 'Bastien Vivès; rencontre; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Abymes : quand l\'oeuvre échappe, trois miroirs se reflètent à l\'infini', 'La mise en abyme est un procédé artistique, d\'abord visuel, qui consiste à insérer dans un tableau une représentation de ce tableau, dans laquelle figure, bien entendu, une image de ce tableau, et ainsi de suite à l\'infini. Par analogie, dans l\'ensemble des œuvres artistiques, on parle de mise en abyme ou d\'effet de miroir notamment lorsqu\'une œuvre met en scène un créateur qui lui-même est occupé à produire une œuvre ou un spectateur occupé à la regarder.', 'http://www.actualitte.com/critiques-bd/abymes-quand-l-oeuvre-echappe-trois-miroirs-se-refletent-a-l-infini-2089.htm', 35, 'Nicolas Ancion', '2013-5-10', 'Abymes; Griffo; Valérie Mangin; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Rouen : parution d\'un livre sur les Dogs, groupe de rock légendaire', 'Cet après midi (samedi 11 mai) Catherine Laboubée présente la sortie officielle d\'un livre sur le groupe de son frère Dominique', 'http://haute-normandie.france3.fr/2013/05/11/rouen-parution-d-un-livre-sur-les-dogs-groupe-de-rock-legendaire-249535.html', 100, 'Richard Plumet', '2013-5-11', 'Catherine Laboubée; bigraphie; Dogs; Musique', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Lecture : “Le temps, le temps” de Martin Suter', 'Derrière ce titre en forme de bégaiement, d\'écho, de pulsation, se cache l\'entreprise insensée de deux voisins d\'une zone pavillonnaire, veufs inconsolables coupés du monde. Fabienne Bussaglia lit un extrait du nouveau roman de Martin Suter.', 'http://www.telerama.fr/livre/lecture-le-temps-le-temps-de-martin-suter,96706.php#xtor=RSS-36', 5, 'Fabienne Bussaglia', '2013-4-28', 'Lecture; Fabienne Bussaglia; Martin Suter; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Le temps, le temps" de Martin Suter chez Bourgois', 'Résumé<br/>Peter Taler peine à continuer à vivre : depuis que son épouse Laura a été tuée au bas de leur immeuble, le chagrin et le désir de vengeance l\'assaillent. Il est toutefois décidé à mener sa propre enquête. Les indices sont faibles. Seule demeure une infime impression du jour tragique : quelque chose, dans son panorama quotidien, n\'est plus pareil...', 'http://www.20minutes.fr/livres/1151929-20130508-le-temps-temps-martin-suter-chez-bourgois-paris-France', 24, 'NULL', '2013-5-8', 'Lecture; Fabienne Bussaglia; Martin Suter; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Beautiful Bastard: "Plus subtil et plus prenant que Fifty Shades of Grey"', 'Si vous devez attaquer un roman érotique, pensez à Beautiful Bastard! Pour le club Sexpress, Antinea43 vous en fait la critique. ', 'http://www.lexpress.fr/culture/livre/le-dernier-page-turner-erotique-est-un-succes_1246492.html', 6, 'Antinea43', '2013-5-12', 'Christina Lauren; érotique; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Après "Beautiful Bastard" et "Cinquante Nuances de Grey", les éditeurs s\'intéressent aux fanfictions', 'Un nouveau "sex-seller" a fait son apparition dans les rayons des librairies françaises. Après le phénomène Cinquante Nuances de Grey, le roman érotique du moment s’appelle Beautiful Bastard et il a au moins deux points communs avec son prédécesseur: Comme 50 Shades, on y parle de sexe... mais Beautiful Bastard est aussi une “fanfiction” transformée en phénomène de librairie.', 'http://www.huffingtonpost.fr/2013/05/07/beautiful-bastard-cinquante-nuances-grey-edition-fanfic_n_3230712.html', 40, 'Lauren Provost', '2013-5-8', 'Christina Lauren; érotique; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'BayDay Leaks: la BD, \'loin des couleurs acidulées du papier glacé\'', 'Depuis maintenant plusieurs semaines, ils sont devenus l\'ennemi public numéro 1 dans le monde des chiroptères. Non, pardon, des phylactères. BayDay Leaks diffuse depuis son Tumblr des câbles meurtriers, puisés dans les dossiers et archives secrets de multiples organisations professionnelles. Chez Glénat, on est démuni ; Delcourt ne sait plus comment arrêter l\'hémorragie... Le constat, partout le même : ces gens n\'ont peur de rien.', 'http://www.actualitte.com/univers-bd/bayday-leaks-la-bd-loin-des-couleurs-acidulees-du-papier-glace-42270.htm', 35, 'Nicolas Gary', '2013-5-10', 'Bayday leaks; tumblr; entretien; interview', '2', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'La saison des BD politiques', 'Deux journalistes, et non des moindres, livrent en même temps leur BD présidentielle. Dans la veine de ce qu\'il avait déjà imaginé sous le quinquennat précédent avec Sarkozy et les femmes et Sarkozy et les riches, Renaud Dély, le directeur de la rédaction du Nouvel Observateur, propose aujourd\'hui Hollande et ses 2 femmes, dessiné comme les précédents par Aurel.', 'http://www.franceinfo.fr/livre/bd-bande-dessinee/la-saison-des-bd-politiques-974543-2013-05-12', 8, 'Jean Christophe Ogier', '2013-5-12', 'Faro; Jungle; Aurel; Drugstore; Marco Paulo; 12 Bis; Pierre Wachs; Alfio Buscaglia; François Ravard; Glénat, BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Ségolène Royal, la mouche du coche', 'En sortant un nouveau livre, l\'ex-candidate à l\'Elysée se rappelle au bon souvenir de François Hollande au moment où il songe à remanier son gouvernement.', 'http://www.lesechos.fr/economie-politique/politique/actu/0202758070141-segolene-royal-la-mouche-du-coche-565776.php', 54, 'Stéphane Dupont', '2013-5-13', 'Ségolène Royal; essai', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le Prix Landerneau Polar 2013 dévoile sa sélection', 'Les libraires des Espaces Culturels E.Leclerc viennent de dévoiler la sélection des ouvrages en lice pour le Prix Landerneau Polar, dont le lauréat sera dévoilé le mardi 21 mai 2013. ', 'http://www.lexpress.fr/culture/livre/le-prix-landerneau-polar-2013-devoile-sa-selection_1248270.html', 6, 'NULL', '2013-5-13', 'Prix Landerneau; Polar; prix littéraire; Leclerc', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Ségolène Royal sur TF1 : avec son livre, elle revient maîtresse de son destin', 'Le Tweetgate lui aura fait du mal, mais pour Ségolène Royal, il est temps de regarder vers l\'avenir. Un an après sa défaite aux législatives, la présidente de la région Poitou-Charentes a amorcé son retour médiatique. Et elle prévient : elle n\'est pas là pour remuer le passé. De l\'avis de notre contributrice, elle semble avoir pris du recul, à raison.', 'http://leplus.nouvelobs.com/contribution/858568-segolene-royal-sur-tf1-avec-son-livre-elle-revient-maitresse-de-son-destin.html', 59, 'Nathalie Blu-Perou', '2013-5-13', 'Ségolène Royal; essai', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Un livre s\'attaque au passé d\'Angela Merkel', 'L\'Allemagne se retourne régulièrement sur son passé. Dans Pelures d\'oignon, Günter Grass évoquait son enfance à Dantzig et son engagement dans les Waffen SS à la fin de la guerre. La révélation récente du passé nazi de Horst Tappert, l\'inspecteur Derrick de la série télévisée homonyme, a été fatale au feuilleton en Allemagne. Aujourd\'hui, un livre promet de faire la lumière sur Angela Merkel.', 'http://www.lemonde.fr/europe/article/2013/05/14/un-livre-s-attaque-au-passe-d-angela-merkel_3197136_3214.html', 13, 'NULL', '2013-5-14', 'Angela Merkel; révélations; Allemagne', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Léo Ferré : le livre d\'Annie Butor est dégueulasse, du verbe dégueuler', 'Un récit sensationnaliste et plein de haine : voilà ce que Pascal Boniface a retenu du livre biographique d\'Annie Butor, \'Comment voulez-vous que j\'oublie : Madeleine et Léo Ferré, 1950-1973\'. La belle-fille de Léo Ferré y raconte sa vie aux côtés du chanteur et de sa deuxième femme, Madeleine. Et Pascal Boniface s\'indigne de ce portrait amer.', 'http://leplus.nouvelobs.com/contribution/858442-leo-ferre-le-livre-d-annie-butor-est-degueulasse-du-verbe-degueuler.html', 59, 'Pascal Boniface', '2013-5-13', 'Léo Ferré; amour; biographie', '4', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '“Gatsby le magnifique”, de Baz Luhrmann, avec Leonardo DiCaprio', 'Cette nouvelle version de “Gatsby le magnifique”, présentée hors compétition en ouverture du Festival de Cannes, s\'avère plus pédagogique et moins flamboyante que prévu.', 'http://www.telerama.fr/festival-de-cannes/2013/gatsby-le-magnifique-de-baz-luhrmann-avec-leonardo-dicaprio,97499.php', 5, 'Frédéric Strauss', '2013-5-15', 'Francis Scott Fitzgerald; Leonardo di Caprio; Festival de Cannes; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Le 66e Festival de Cannes s’ouvre avec Leo/«Gatsby» les magnifiques', 'Le 66e festival de Cannes, plus grande manifestation cinématographique au monde, ouvre ses portes mercredi soir forcément sous le signe du glamour, avec la présence de nombreuses stars emmenées par Leonardo DiCaprio, venu présenter «Gatsby le magnifique».', 'http://www.liberation.fr/societe/2013/05/15/le-66e-festival-de-cannes-s-ouvre-avec-leogatsby-les-magnifiques_903149', 39, 'AFP', '2013-5-15', 'Francis Scott Fitzgerald; Leonardo di Caprio; Festival de Cannes; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Livre : La chronique de David Medioni – Nécrologie', 'Paul Cleave fut l’un des invités de l’édition 2012 du Salon Internationale du Livre de Poche de Saint-Maur-des-Fossés. Cette semaine David Medioni nous présente son nouvel opus « Nécrologie » aux éditions Sonatine.', 'http://www.lesdeblogueurs.tv/culture/livre-la-chronique-de-david-medioni-necrologie/', 23, 'David Médioni', '2013-5-4', 'Paul Cleave; thriller; suspens', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'BD: Boulet lance ses bulles en Amérique du Nord', 'Boulet, l\'auteur vedette français de bande dessinée, est parti ce printemps à la conquête du Nouveau Monde, avec son premier livre en anglais destiné exclusivement à ses fans américains. Le jeune créateur a terminé dimanche à Toronto une tournée d\'un peu plus d\'un mois en Amérique du Nord.', 'http://www.leparisien.fr/flash-actualite-culture/bd-boulet-lance-ses-bulles-en-amerique-du-nord-14-05-2013-2801891.php', 37, 'AFP', '2013-5-14', 'Boulet; BD; bande dessinée', '3', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Roman érotique, BD ou manga inspirent les cinéastes', 'Le 66e Festival de Cannes fait la part belle aux adaptations de livres, source d\'inspiration inépuisable des cinéastes, avec cette année pléthore de films tirés de monuments de la littérature, comme le très attendu Gatsby, mais aussi de romans récents, de BD ou de manga.', 'http://www.7sur7.be/7s7/fr/1777/Deborah-Laurent-envoyee-speciale-a-Cannes/article/detail/1631691/2013/05/13/Roman-erotique-BD-ou-manga-inspirent-les-cineastes.dhtml', 101, 'Déborah Laurent ', '2013-5-13', 'Francis Scott Fitzgerald; Leonardo di Caprio; Festival de Cannes; roman', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Marie-Ségolène du Poitou rêve de retrouver la cour"', 'Ségolène Royal est très présente ces derniers temps. A l\'occasion de la sortie de son livre, elle assure qu\'une entrée au gouvernement "n\'est pas d\'actualité". Notre contributeur Hervé Karleskind pense pourtant qu\'elle rêve d\'un poste de ministre, raconte-t-il avec la plume de Madame de Sévigné. ', 'http://www.lexpress.fr/actualite/le-livre-de-la-duchesse-marie-segolene-signera-t-il-son-retour-en-grace_1248005.html', 6, 'Bongobi', '2013-5-13', 'Ségolène Royal; essai', '8', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Purgatoire des innocents: intense jusqu\'à la dernière page', '« Chaque être humain est un ennemi, une proie ou un esclave. Rien d\'autre. » Avec Purgatoire des innocents, Karine Giebel continue à scruter les côtés les plus sombres de l\'être humain, comme elle l\'avait fait avec son roman précédent, Juste une ombre (le meilleur parmi les meilleurs dans notre top polars 2012). Vol, fuite, prise d\'otage, rapt et bien pire encore. Le polar porte à son paroxysme les possibilités de l\'horreur. Aussi intense qu\'absolu. Bienvenue en enfer.', 'http://quebec.huffingtonpost.ca/daniel-marois/critique-purgatoire-des-innoncents_b_3282192.html?utm_hp_ref=divertissement', 102, 'Daniel Marois', '2013-5-16', 'Fleuve Noir; Karine Giebel; Purgatoire Des Innocents; Polar', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Souvenance", un plaisir de lecture signé Pauline Delpech', 'Pauline Delpech est devenue la fille de Michel Delpech à l’âge de quatre ans, lorsque sa maman a rencontré le chanteur. Auteur de romans policiers, Pauline se laisse aller pour la première fois, à plus d’intimité. Un roman qui trouve son essence, dans la propre existence de l’auteur.', 'http://www.rtbf.be/culture/article/detail_souvenance-un-plaisir-de-lecture-signe-pauline-delpech?id=7993707', 83, 'Christine Pinchart', '2013-5-13', 'Pauline Delpech; Michel Delpech; roman; polar', '22', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, '"Le code du Démon" de Adam Blake chez Ma éditions', 'Adam Blake est le pseudonyme d\'un scénariste de deux séries de bandes dessinées les plus populaires au monde : X-Men et Fantastic Four. Son premier thriller, L\'Évangile des Assassins, met également en scène Heather Kennedy et Léo Tillman.', 'http://www.20minutes.fr/livres/1153875-20130513-le-code-demon-adam-blake-chez-editions-paris-France', 24, 'NULL', '2013-5-13', 'Adam Blake; polar; thriller', '21', '0', '0');
INSERT INTO `s1b_pressreviews` (`book_id`, `title`, `text`, `link`, `media_id`, `author`, `date`, `keywords`, `tag_id`, `is_validated`, `type`) VALUES (NULL, 'Francis Scott Fitzgerald se livre à nouveau', 'Une biographie, une série d\'entretiens inédits et une bande dessinée... Trois ouvrages célèbrent Francis Scott Fitzgerald, le père du magnifique «Gatsby»…', 'http://www.20minutes.fr/cinema/1154771-20130514-francis-scott-fitzgerald-livre-a-nouveau', 24, 'Sarah Gandillot', '2013-5-14', 'Francis Scott Fitzgerald; Leonardo di Caprio; Festival de Cannes; roman', '22', '0', '0');
